    47→/**
    48→ * Mostrar modal de créditos IA.
    49→ *
    50→ * @return {void}
    51→ * @author José Manuel Jiménez
    52→ * @author Sergio Vargas
    53→ * @author Daniel Beltrán
    54→ */
    55→function mostrarModalCreditosIA(servicio_ia_id) {
    56→    // Verificar si el usuario optó por no mostrar el modal
    57→    if (localStorage.getItem('no_mostrar_modal_creditos_ia') === 'true') {
    58→        mostro_modal_creditos_ia = true;
    59→        if (funcion_ejecucion_ia) {
    60→            funcion_ejecucion_ia();
    61→            funcion_ejecucion_ia = null;
    62→        }
    63→        mostro_modal_creditos_ia = false;
    64→        return;
    65→    }
    66→
    67→    $.ajax({
    68→        url     : `/creditos-disponibles-empresas/${$('#empresa_sesion_id').val()}`,
    69→        method  : 'GET',
    70→        dataType: 'json',
    71→        success : function (respuesta_empresa) {
    72→            if (respuesta_empresa.data?.creditos > 0) {
    73→                $.ajax({
    74→                    url     : `/servicios-ia/${servicio_ia_id}/creditos`,
    75→                    method  : 'GET',
    76→                    dataType: 'json',
    77→                    success : function (respuesta) {
    78→                        if (respuesta?.data?.consume_creditos && respuesta?.data?.consume_creditos <= respuesta_empresa.data.creditos) {
    79→                            $('#cantidad-creditos-descontar-modal').text(respuesta.data.consume_creditos);
    80→                            $('#modal-consumo-creditos-ia').modal('show');
    81→                            mostro_modal_creditos_ia = true;
    82→                            modals_abiertos_ia = $('.modal.show').not('#modal-consumo-creditos-ia').not('#modal-generando-ia');
    83→                            $(modals_abiertos_ia).addClass('d-none');
    84→                        } else {
    85→                            mostrarToastSolicitarCreditosIA();
    86→                        }
    87→                    }
    88→                });
    89→            } else {
    90→                mostrarToastSolicitarCreditosIA();
    91→            }
    92→        }
    93→    });
    94→}
