   216→/**
   217→ * Convertir fecha de formato YYYY-MM-DD a DD-MM-YYYY.
   218→ *
   219→ * @param {string} fecha Fecha en formato ISO (YYYY-MM-DD).
   220→ * @returns {string} Fecha convertida a formato DD-MM-YYYY.
   221→ * @author Andres Morales
   222→ * @author Sergio Vargas
   223→ */
   224→const convertirFecha = fecha => {
   225→    if (!fecha) return '';
   226→    fecha = String(fecha).substring(0, 10);
   227→    return fecha.includes('-') ? fecha.split('-').reverse().join('-') : 'Formato incorrecto';
   228→};
   229→
   230→/**
   231→ * Formatear fecha a formato dd/mm/yyyy - hh:mm AM/PM.  Ejemplo: 01/Feb/2022 - 12:00 PM
   232→ *
   233→ * @param {string} fecha_entrada fecha en formato ISO
   234→ * @returns {string} fecha y hora formateada
   235→ * @author Andres Morales
   236→ */
   237→function formatearFecha(fecha_entrada) {
   238→    var fecha = new Date(fecha_entrada);
   239→    var dia = fecha.getDate();
   240→    var mes = fecha.toLocaleString('es-ES', { month: 'short' });
   241→    var anio = fecha.getFullYear();
   242→    let hora = fecha.getHours();
   243→    let minuto = fecha.getMinutes();
   244→    let ampm = hora >= 12 ? 'PM' : 'AM';
   245→    hora = hora % 12 || 12;
   246→    minuto = minuto.toString().padStart(2, '0');
   247→    return `${dia}/${mes}/${anio} - ${hora}:${minuto} ${ampm}`;
   248→}
   249→
   250→function formatearFecha2(fecha_entrada) {
   251→    let partes = fecha_entrada.split(' ');
   252→    let fecha = partes[0].split('-');
   253→    let hora = partes[1];
   254→    let fechaISO = `${fecha[2]}-${fecha[1]}-${fecha[0]}T${hora}`;
   255→
   256→    let date = new Date(fechaISO);
   257→
   258→    if (isNaN(date.getTime())) {
   259→        return 'Fecha inválida';
   260→    }
   261→
   262→    let dia = date.getDate();
   263→    let mes = date.toLocaleString('eu-EU', { month: 'short' });
   264→    let anio = date.getFullYear();
   265→    return `${dia}/${mes}/${anio}`;
   266→}
