   341→/**
   342→ * Funcion para hacer el slug de un texto desde jquery.
   343→ *
   344→ * @returns {void}
   345→ * @author Andres Morales
   346→ * @deprecated No utilizar para cargar logos de pruebas, usar el slug ligado a la prueba en su lugar.
   347→ */
   348→function slugify(text) {
   349→    return $.trim(text)
   350→        .toLowerCase()
   351→        .normalize("NFD")
   352→        .replace(/[\u0300-\u036f]/g, "")
   353→        .replace(/[^a-z0-9 -]/g, "")
   354→        .replace(/\s+/g, "-")
   355→        .replace(/-+/g, "-");
   356→}
