   358→/**
   359→ * Función generica para mostrar mensajes de respuesta
   360→ *
   361→ * @param {string} tipo_alerta define el color de la alerta segun alerts de botstrap.
   362→ * @param {string} texto_1 Texto que mostrara la alerta.
   363→ * @param {string} texto_2 Texto secundario en la alerta.
   364→ * @param {string} accion Acción adicional(por definir).
   365→ * @returns {void}
   366→ * @author José Manuel Jiménez
   367→ * @author Andres Gonzalez
   368→ */
   369→function mostrarToast(tipo_alerta, texto_1, texto_2 = '', accion = '', icono2 = '') {
   370→    let icono = '';
   371→    let texto_color = 'text-white';
   372→    if (accion != '') {
   373→        accion = `<div class="col-auto p-0 text-label-1">${accion}</div>`;
   374→    }
   375→
   376→    if (tipo_alerta == 'success') {
   377→        icono = 'bi bi-check-circle';
   378→    }
   379→
   380→    if (tipo_alerta == 'neutral') {
   381→        icono = 'bi bi-info-circle';
   382→    }
   383→
   384→    if (tipo_alerta == 'warning') {
   385→        icono = 'bi bi-info-circle';
   386→        texto_color = "text-red-letras";
   387→    }
   388→
   389→    if (tipo_alerta == 'danger') {
   390→        icono = 'bi bi-x-circle';
   391→    }
   392→
   393→    if (icono2 != '') {
   394→        icono = icono2;
   395→    }
   396→
   397→    if (texto_2 != '') {
   398→        texto_2 = `<div class="col-12 p-0 text-label-1 ${texto_color}">${texto_2}</div>`;
   399→    }
   400→
   401→    let alert_toast = $(`
   402→        <div class="alert toast-${tipo_alerta} alert-dismissible fade show d-flex align-items-center pe-3 py-3 me-3" role="alert" style="display:none">
   403→            <div class="col-auto">
   404→                <span class="${icono} ${texto_color}"></span>
   405→            </div>
   406→            <div class="col px-3">
   407→                <div class="row m-0 ${texto_color}">
   408→                    <div class="col-12 p-0 text-label-1 ${texto_color}">
   409→                        ${texto_1}
   410→                    </div>
   411→                    ${texto_2}
   412→                </div>
   413→            </div>
   414→            ${accion}
   415→            <div class="col-auto ms-3 pointer" data-bs-dismiss="alert">
   416→                <span class="bi bi-x ${texto_color}"></span>
   417→            </div>
   418→        </div>
   419→    `);
   420→    $('#contenedor-alert-toast').append(alert_toast);
   421→    alert_toast.fadeIn();
   422→    setTimeout(function() {
   423→        alert_toast.fadeOut();
   424→        alert_toast.alert('close');
   425→    }, 5000);
   426→}
