   438→/**
   439→ * Formatear fecha.
   440→ *
   441→ * @param {*} fecha Fecha a formatear.
   442→ * @param {boolean} incluir_segundos Indica si se debe incluir los segundos en la hora.
   443→ * @param {string} formato Formato en el que se retornará la fecha.
   444→ * @param {string} separador Símbolo que se usará para separar días, meses y años.
   445→ * @returns {Object} Objeto con la fecha y hora formateada.
   446→ * @author Daniel Beltrán
   447→ */
   448→function formatearFechaConceptual(fecha, incluir_segundos = false, formato = 'Ymd', separador = '-') {
   449→    const NUEVA_FECHA = new Date(fecha);
   450→    const ANO         = NUEVA_FECHA.getFullYear();
   451→    const MES         = String(NUEVA_FECHA.getMonth() + 1).padStart(2, '0');
   452→    const DIA         = String(NUEVA_FECHA.getDate()).padStart(2, '0');
   453→    const HORAS       = String(NUEVA_FECHA.getHours()).padStart(2, '0');
   454→    const MINUTOS     = String(NUEVA_FECHA.getMinutes()).padStart(2, '0');
   455→    const SEGUNDOS    = String(NUEVA_FECHA.getSeconds()).padStart(2, '0');
   456→
   457→    let fecha_formateada = `${ANO}${separador}${MES}${separador}${DIA}`;
   458→
   459→    if (formato === 'dmY') {
   460→        fecha_formateada = `${DIA}${separador}${MES}${separador}${ANO}`;
   461→    }
   462→
   463→    return {
   464→        fecha: fecha_formateada,
   465→        hora : incluir_segundos ? `${HORAS}:${MINUTOS}:${SEGUNDOS}` : `${HORAS}:${MINUTOS}`
   466→    };
   467→}
   468→
   469→/**
   470→ * Formatear fecha a May. 2023.
   471→ *
   472→ * @param {string} fechaISO Fecha a formatear.
   473→ * @returns {string} Objeto con la fecha y hora formateada.
   474→ * @author Andres Morales
   475→ */
   476→function formatearFechaAnioMes(fechaISO) {
   477→    const fecha = new Date(fechaISO);
   478→    const opciones = { month: 'short', year: 'numeric' };
   479→    let fecha_formateada = fecha.toLocaleDateString('es-ES', opciones);
   480→
   481→    fecha_formateada = fecha_formateada.replace(
   482→        /^([a-zñ]+)(\s\d{4})$/,
   483→        (_, mes, anio) => `${mes.charAt(0).toUpperCase() + mes.slice(1)}. ${anio.trim()}`
   484→    );
   485→
   486→    return fecha_formateada;
   487→}
