   489→/**
   490→ * Formatear un numero a moneda.
   491→ *
   492→ * @param {string} valor Numero.
   493→ * @returns {string} Numero formateado.
   494→ * @author Andres Morales
   495→ */
   496→function formatearMonedaCOP(valor) {
   497→    return new Intl.NumberFormat('es-CO', {
   498→        style: 'currency',
   499→        currency: 'COP',
   500→        minimumFractionDigits: 0,
   501→        maximumFractionDigits: 0
   502→    }).format(valor);
   503→}
