   505→/**
   506→ * Formatear fecha a un formato d/m/Y hh:mm:ss entendible para el usuario.
   507→ *
   508→ * @param {*} fecha_inicial Fecha a formatear.
   509→ * @returns {String} String con la fecha y hora formateada.
   510→ * @author José Manuel Jiménez
   511→ */
   512→function fechaMostrar(fecha_inicial) {
   513→    if (!fecha_inicial) {
   514→        return "";
   515→    }
   516→    const fecha = new Date(fecha_inicial);
   517→    if (isNaN(fecha.getTime())) {
   518→        return "";
   519→    }
   520→    const dia = String(fecha.getUTCDate()).padStart(2, '0');
   521→    const mes = String(fecha.getUTCMonth() + 1).padStart(2, '0');
   522→    const anio = fecha.getUTCFullYear();
   523→    // Verificar si el string incluye información de hora (espacio, T, o dos puntos)
   524→    if (!fecha_inicial.includes(" ") && !fecha_inicial.includes("T") && !fecha_inicial.includes(":")) {
   525→        return `${dia}/${mes}/${anio}`;
   526→    }
   527→    const horas = String(fecha.getUTCHours()).padStart(2, '0');
   528→    const minutos = String(fecha.getUTCMinutes()).padStart(2, '0');
   529→    const segundos = String(fecha.getUTCSeconds()).padStart(2, '0');
   530→    return `${dia}/${mes}/${anio} ${horas}:${minutos}:${segundos}`;
   531→}
