   533→/**
   534→ * Descargar Excel.
   535→ *
   536→ * @param {string} ruta Ruta desde donde se descarga el archivo.
   537→ * @param {string} nombre_archivo Nombre del archivo descargado.
   538→ * @param {boolean} marca_tiempo Indica si el nombre del archivo debe llevar marca de tiempo.
   539→ * @param {Object} parametros Parámetros a enviar en la petición.
   540→ * @returns {void}
   541→ * @author Daniel Beltrán
   542→ */
   543→async function descargarExcel(ruta, nombre_archivo, marca_tiempo = true, parametros = {}) {
   544→    parametros._token = $('meta[name="csrf-token"]').attr('content');
   545→
   546→    await $.ajax({
   547→        type     : 'POST',
   548→        url      : `/${ruta}`,
   549→        data     : parametros,
   550→        xhrFields: {
   551→            responseType: 'blob'
   552→        },
   553→        success: function (respuesta) {
   554→            // Crear una URL para el Blob
   555→            let url = window.URL.createObjectURL(respuesta); // 'respuesta' es el Blob
   556→            let a   = document.createElement('a'); // Crea un nuevo elemento <a> para iniciar la descarga
   557→            a.href  = url;
   558→            const fecha_actual     = new Date();
   559→            const dia              = fecha_actual.getDate();
   560→            const mes              = fecha_actual.getMonth() + 1;
   561→            const ano              = fecha_actual.getFullYear();
   562→            const hora             = fecha_actual.getHours();
   563→            const minutos          = fecha_actual.getMinutes();
   564→            const segundos         = fecha_actual.getSeconds();
   565→            const fecha_formateada = `${dia}${mes}${ano}${hora}${minutos}${segundos}`;
   566→
   567→            a.download = marca_tiempo ? `${nombre_archivo}-${fecha_formateada}.xlsx` : `${nombre_archivo}.xlsx`;
   568→            document.body.appendChild(a);
   569→            a.click();
   570→            window.URL.revokeObjectURL(url);
   571→            document.body.removeChild(a);
   572→        }
   573→    }).then(() => {
   574→        mostrarToast('success', 'Archivo descargado correctamente.');
   575→    }).catch((error) => {
   576→        mostrarToast('danger', 'Ocurrió un error al descargar el archivo.');
   577→    });
   578→}
