   580→/**
   581→ * Convierte un string a slug.
   582→ *
   583→ * @param {string} str
   584→ * @returns {string}
   585→ * @author Fahibram Cárcamo
   586→ */
   587→function slug(str) {
   588→
   589→    // Quita espacios en blanco
   590→    str = str.replace(/^\s+|\s+$/g, "")
   591→
   592→    // Convierte el string a minusculas
   593→    str = str.toLowerCase()
   594→
   595→    // Quita los caracteres no alfanuméricos
   596→    str = str.replace(/[^a-z0-9 -]/g, "")
   597→
   598→    // Reemplaza los espacio con guiones
   599→    str = str.replace(/\s+/g, "-")
   600→
   601→    // Remueve los guiones consecutivos
   602→    str = str.replace(/-+/g, "-")
   603→
   604→    return str
   605→}
