   607→/**
   608→ * Compara los elementos de dos arreglos y retorna `true` si son iguales y `false` en caso contrario
   609→ * (Este función está pensada para comparar arreglos unidimensionales).
   610→ *
   611→ * @param {array} primer_arreglo
   612→ * @param {array} segundo_arreglo
   613→ * @param {boolean} comparar_orden
   614→ * @return {boolean}
   615→ * @author Fahibram Cárcamo
   616→ */
   617→function sonArreglosIguales(primer_arreglo, segundo_arreglo, comparar_orden = false) {
   618→
   619→    if (!comparar_orden) {
   620→        primer_arreglo.sort()
   621→        segundo_arreglo.sort()
   622→    }
   623→
   624→    if (primer_arreglo.length !== segundo_arreglo.length) return false
   625→
   626→    for (let i = 0; i < primer_arreglo.length; i++) {
   627→        if (primer_arreglo[i] !== segundo_arreglo[i]) return false
   628→    }
   629→
   630→    return true
   631→}
