   674→ */
   675→async function enviarOrdenSuplantacionRolEmpresa (callback_exito, url, _token, empresa_id = null, rol_id = null, callback_error = null) {
   676→
   677→    let condicion_suplantacion = rol_id === null
   678→        ? empresa_id === null
   679→            ? true
   680→            : parseInt($('#empresa_sesion_id').val()) !== parseInt(empresa_id)
   681→        : parseInt($('#rol_sesion_id').val()) !== parseInt(rol_id) && parseInt($('#empresa_sesion_id').val()) !== parseInt(empresa_id);
   682→
   683→    if (condicion_suplantacion) {
   684→        let data = { _token };
   685→        if (empresa_id !== null) data.empresa_id = empresa_id;
   686→        if (rol_id !== null) data.rol_id = rol_id;
   687→
   688→        await $.ajax({ type: 'POST', url, data })
   689→            .then((respuesta, status, xhr) => {
   690→                if (xhr.status >= 200 && xhr.status < 300 && callback_exito !== null) callback_exito();
   691→            })
   692→            .catch(error => {
   693→                let swal_titulo, swal_texto;
   694→
   695→                if (error.status === 404) {
   696→                    if (error.responseJSON.mensaje) {
   697→                        swal_titulo = 'Algo salió mal';
   698→                        if (error.responseJSON.mensaje === 'no existe rol_suplantar') {
   699→                            swal_texto = 'El rol no existe.';
   700→                        } else if (error.responseJSON.mensaje === 'no existe empresa_suplantar') {
   701→                            swal_texto = 'La empresa no existe.';
   702→                        } else if (error.responseJSON.mensaje === 'no existe usuario_suplantar') {
   703→                            swal_texto = 'El usuario no existe.';
   704→                        } else {
   705→                            swal_texto = '';
   706→                        }
   707→                    } else if (error.responseJSON.empresa && error.responseJSON.rol) {
   708→                        swal_titulo = 'No fué posible cambiar de empresa';
   709→                        swal_texto = error.responseJSON.rol === 'Administrador general' || error.responseJSON.rol === 'Propietario'
   710→                            ? `La empresa ${error.responseJSON.empresa} no tiene ningún usuario con el rol Administrador general o Propietario.`
   711→                            : `La empresa ${error.responseJSON.empresa} no tiene ningún usuario con el rol ${error.responseJSON.rol}.`;
   712→                    } else {
   713→                        swal_titulo = 'Algo salió mal';
   714→                        swal_texto = error.responseJSON.errors;
   715→                    }
   716→                } else {
   717→                    swal_titulo = 'Algo salió mal';
   718→                    swal_texto = '';
   719→                }
   720→
   721→                if (callback_error !== null) callback_error();
   722→
   723→                Swal.fire({
   724→                    title: swal_titulo,
   725→                    text: swal_texto,
   726→                    icon: "error",
   727→                    confirmButtonText: "Aceptar"
   728→                });
   729→            });
   730→    }
   731→}
