   757→/**
   758→ * Retornar la diferencia en días de dos fechas dadas.
   759→ *
   760→ * @param {string} fecha_inicial Fecha inicial
   761→ * @param {string} fecha_final Fecha final
   762→ * @param {bool} valor_absoluto Retorna la diferencia en valor absoluto
   763→ * @returns {number}
   764→ * @author Fahibram Cárcamo
   765→ * @author Andres Morales
   766→ */
   767→function diferenciaFechasDias(fecha_inicial, fecha_final, valor_absoluto = false, sumar_dia = true) {
   768→    let fecha1 = new Date(fecha_inicial);
   769→    let fecha2 = new Date(fecha_final);
   770→
   771→    fecha1.setHours(0, 0, 0, 0);
   772→    fecha2.setHours(0, 0, 0, 0);
   773→
   774→    let diferencia_milisegundos = fecha2.getTime() - fecha1.getTime();
   775→
   776→    let diferencia_dias = diferencia_milisegundos / (1000 * 60 * 60 * 24);
   777→    let resultado = diferencia_dias;
   778→    if (sumar_dia) {
   779→        resultado = diferencia_dias + 1;
   780→    }
   781→
   782→    return valor_absoluto ? Math.abs(resultado) : resultado;
   783→}
