   785→/**
   786→ * Retornar `true` si una fecha dada está dentro de un rango de fechas dadas, `false` en otro caso.
   787→ *
   788→ * @param {string} fecha Fecha dada
   789→ * @param {string} fecha_desde Fecha inicial del rango
   790→ * @param {string} fecha_hasta Fecha final del rango
   791→ * @returns {boolean}
   792→ * @author Fahibram Cárcamo
   793→ */
   794→function fechaDentroDelRango(fecha, fecha_desde, fecha_hasta) {
   795→    fecha = new Date(fecha);
   796→    fecha_desde = new Date(fecha_desde);
   797→    fecha_hasta = new Date(fecha_hasta);
   798→
   799→    if (isNaN(fecha) || isNaN(fecha_desde) || isNaN(fecha_hasta)) {
   800→        throw new Error('Una o más fechas no son válidas.');
   801→    }
   802→
   803→    return fecha >= fecha_desde && fecha <= fecha_hasta;
   804→}
