   806→/**
   807→ * Descuenta el tiempo para las respuestas.
   808→ *
   809→ * @param id - ID del elemento HTML donde mostrar el tiempo
   810→ * @param tiempo - Tiempo inicial en segundos
   811→ * @author Jair Carvajal
   812→ */
   813→var tiempos_usados = {};
   814→
   815→function descuentoTiempo(id, tiempo, callback, onTick) {
   816→    var $elemento = $('#' + id);
   817→    if ($elemento.length === 0) {
   818→        console.error('Elemento con id "' + id + '" no encontrado.');
   819→        return;
   820→    }
   821→    if (tiempos_usados[id]) {
   822→        clearInterval(tiempos_usados[id]);
   823→    }
   824→    let tiempoTotal = tiempo;
   825→    tiempos_usados[id] = setInterval(function() {
   826→        if (tiempo <= 0) {
   827→            clearInterval(tiempos_usados[id]);
   828→            delete tiempos_usados[id];
   829→            $elemento.text('Tiempo finalizado');
   830→            if (window.tiempoFinalizado()) {
   831→                tiempoFinalizado();
   832→            }
   833→            if (typeof callback === 'function') {
   834→                callback();
   835→            }
   836→            return;
   837→        }
   838→
   839→        var minutos = Math.floor(tiempo / 60);
   840→        var segundos = tiempo % 60;
   841→
   842→        var textoTiempo = minutos + ':' + (segundos < 10 ? '0' : '') + segundos + '<i class="bi bi-alarm ms-2"></i>';
   843→
   844→        if (tiempo <= 15) {
   845→            $elemento.addClass('text-state-red');
   846→            $elemento.removeClass('text-state-green');
   847→        } else {
   848→            $elemento.addClass('text-state-green');
   849→            $elemento.removeClass('text-state-red');
   850→        }
   851→
   852→        $elemento.html(textoTiempo);
   853→
   854→        var tiempoUsado = tiempoTotal - tiempo;
   855→
   856→        if (typeof onTick === 'function') {
   857→            onTick((tiempoUsado % 5));
   858→        }
   859→        tiempo--;
   860→    }, 1000);
   861→}
