   877→/**
   878→ * Genera un SVG de la imagen de usuario.
   879→ *
   880→ * @param id ID del usuario.
   881→ * @param nombres Nombres del usuario.
   882→ * @param apellidos Apellidos del usuario.
   883→ * @return image
   884→ * @author Andres Morales
   885→ */
   886→function crearImagenUsuarioLetras(id, nombres, apellidos) {
   887→    var tamano = 200;
   888→    var iniciales = (nombres[0] + apellidos[0]).toUpperCase();
   889→    var hash = md5(id);
   890→
   891→    var r = parseInt(hash.substring(0, 2), 16);
   892→    var g = parseInt(hash.substring(2, 4), 16);
   893→    var b = parseInt(hash.substring(4, 6), 16);
   894→    var colorFondo = `rgb(${r},${g},${b})`;
   895→
   896→    var luminancia = 0.299 * r + 0.587 * g + 0.114 * b;
   897→    var colorTexto = luminancia > 150 ? "#000000" : "#FFFFFF";
   898→
   899→    // SVG como texto
   900→    var svg = `
   901→    <svg xmlns="http://www.w3.org/2000/svg" width="${tamano}" height="${tamano}">
   902→        <rect width="100%" height="100%" fill="${colorFondo}" />
   903→        <text x="50%" y="50%" dy=".35em"
   904→            text-anchor="middle"
   905→            font-family="Lato, sans-serif"
   906→            font-size="${tamano * 0.4}"
   907→            fill="${colorTexto}">
   908→            ${iniciales}
   909→        </text>
   910→    </svg>`;
   911→    var svgEncoded = encodeURIComponent(svg)
   912→        .replace(/'/g, "%27")
   913→        .replace(/"/g, "%22");
   914→
   915→    var dataUri = `data:image/svg+xml,${svgEncoded}`;
   916→    return dataUri;
   917→}
