  1050→ *
  1051→ * @return void
  1052→ * @author Andres Gonzalez
  1053→ */
  1054→function configurarWebSocketNotificaciones() {
  1055→    try {
  1056→        if (!window.configuracion_reverbg) {
  1057→            return;
  1058→        }
  1059→
  1060→        const PUSHER = new Pusher(window.configuracion_reverbg.appKey, {
  1061→            wsHost: window.configuracion_reverbg.host,
  1062→            wsPort: window.configuracion_reverbg.port,
  1063→            wssPort: window.configuracion_reverbg.port,
  1064→            cluster: 'mt1',
  1065→            forceTLS: false,
  1066→            enabledTransports: ['ws', 'wss'],
  1067→            authEndpoint: '/broadcasting-auth',
  1068→            auth: {
  1069→                headers: {
  1070→                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
  1071→                }
  1072→            }
  1073→        });
  1074→
  1075→        PUSHER.connection.bind('failed', function() {
  1076→            console.error('❌ Conexión WebSocket falló');
  1077→        });
  1078→
  1079→        PUSHER.connection.bind('error', function(error) {
  1080→            console.error('❌ Error de WebSocket:', error);
  1081→        });
  1082→
  1083→        let usuario_id = window.configuracion_reverbg.usuario_id;
  1084→
  1085→        if (!usuario_id) {
  1086→            return;
  1087→        }
  1088→
  1089→        const NOMBRE_CANAL = `private-App.Models.Usuarios.User.${usuario_id}`;
  1090→        const CANAL = PUSHER.subscribe(NOMBRE_CANAL);
  1091→
  1092→        CANAL.bind('pusher:subscription_error', function(error) {
  1093→            console.error('❌ Error al suscribirse al canal:', error);
  1094→        });
  1095→
  1096→        // Escuchar eventos específicos de notificaciones
  1097→        CANAL.bind('Illuminate\\Notifications\\Events\\BroadcastNotificationCreated', function(data) {
  1098→            if (data.tipo === 'nueva_notificacion') {
  1099→                audio_notificacion.play();
