  1163→/**
  1164→ * Obtener la edad actual de un usuario según su fecha de nacimiento.
  1165→ *
  1166→ * @param {string} fecha_nacimiento
  1167→ * @returns {string}
  1168→ * @author José Manuel Jiménez
  1169→ * @author Fahibram Cárcamo
  1170→ * @author Andres Morales
  1171→ */
  1172→function obtenerEdad(fecha_nacimiento) {
  1173→    if (!fecha_nacimiento) return "N/A";
  1174→    let hoy = new Date();
  1175→    let nacimiento = new Date(fecha_nacimiento);
  1176→    let edad = hoy.getFullYear() - nacimiento.getFullYear();
  1177→    let mes = hoy.getMonth() - nacimiento.getMonth();
  1178→    if (mes < 0 || (mes === 0 && hoy.getDate() < nacimiento.getDate())) edad--;
  1179→    return edad + " años";
  1180→}
