  1256→            initialCountry: 'co',
  1257→            separateDialCode: true,
  1258→            autoInsertDialCode: false,
  1259→            autoPlaceholder: 'aggressive',
  1260→            dropdownContainer: dropdown_dinamico,
  1261→            customPlaceholder: (placeholder, country) => placeholder_input_celular,
  1262→            loadUtils: () => import('https://cdn.jsdelivr.net/npm/intl-tel-input@25.4.2/build/js/utils.js'),
  1263→            onlyCountries: [
  1264→                'CO', // Colombia
  1265→                'EC', // Ecuador
  1266→                'AR', // Argentina
  1267→                'CL', // Chile
  1268→                'VE', // Venezuela
  1269→                'PE', // Perú
  1270→                'BO', // Bolivia
  1271→                'UY', // Uruguay
  1272→                'PY', // Paraguay
  1273→                'PA', // Panamá
  1274→                'MX', // México
  1275→                'CR', // Costa Rica
  1276→                // 'PR', // Puerto Rico
  1277→                'CU', // Cuba
  1278→                // 'DO', // República Dominicana
  1279→                'SV', // El Salvador
  1280→                'GT', // Guatemala
  1281→                'HN', // Honduras
  1282→                'NI', // Nicaragua
  1283→                'ES', // España
  1284→                'US', // Estados Unidos
  1285→                'GQ'  // Guinea Ecuatorial
  1286→            ]
  1287→        });
  1288→
  1289→        if (obligatorio) $(`#${input_celular_id}`).attr('required', true);
  1290→
  1291→        return campo_celular;
  1292→    }
  1293→
  1294→    return null;
  1295→}
