  1333→/**
  1334→ * Convierte imágenes de base64 a Blob.
  1335→ *
  1336→ * @param {string} cadena_base_64 Base 64 de la imagen.
  1337→ * @returns {Promise<Array>}
  1338→ * @author Andres Gonzalez
  1339→ */
  1340→function imagenBase64Blob(cadena_base_64) {
  1341→    if (!cadena_base_64 || !cadena_base_64.startsWith('data:image')) return null;
  1342→    let datos_base_64 = cadena_base_64.split(',')[1];
  1343→    let tipo_mime = cadena_base_64.match(/data:(.*?);base64/)[1];
  1344→    let caracteres_binarios = atob(datos_base_64);
  1345→    let arreglo_bytes = new Array(caracteres_binarios.length).fill(0).map((_, i) => caracteres_binarios.charCodeAt(i));
  1346→    let bytes_uint8 = new Uint8Array(arreglo_bytes);
  1347→    return new Blob([bytes_uint8], { type: tipo_mime });
  1348→}
