  1351→/**
  1352→ * Limpiar estructuras html innecesarias en el check editor.
  1353→ *
  1354→ * @param {string} html - Cadena HTML generada por CKEditor a limpiar
  1355→ * @returns {string} HTML limpio sin elementos específicos de CKEditor
  1356→ * @author Andres Gonzalez
  1357→ * @author Jair Carvajal
  1358→ */
  1359→function limpiarHTMLCKEditor(html) {
  1360→    // Remover todos los elementos con clases de CKEditor
  1361→    html = html.replace(/<div class="ck[^"]*"[^>]*>.*?<\/div>/gs, '');
  1362→    html = html.replace(/<span class="[^"]*ck[^"]*"[^>]*>/gi, '<span>');
  1363→
  1364→    // Limpiar spans de imagen inline
  1365→    html = html.replace(/<span class="image-inline[^"]*"[^>]*>/gi, '');
  1366→    html = html.replace(/<\/span>/g, '');
  1367→
  1368→    // Remover elementos de upload y widgets
  1369→    html = html.replace(/<div class="ck-image-upload[^"]*"[^>]*>.*?<\/div>/gs, '');
  1370→    html = html.replace(/<div class="ck-fake-selection[^"]*"[^>]*>.*?<\/div>/gs, '');
  1371→
  1372→    // Remover atributos innecesarios
  1373→    html = html.replace(/contenteditable="[^"]*"/gi, '');
  1374→    html = html.replace(/data-cke-filler="[^"]*"/gi, '');
  1375→
  1376→    // Limpiar párrafos vacíos con br
  1377→    html = html.replace(/<p><br[^>]*><\/p>/gi, '');
  1378→    html = html.replace(/<br data-cke[^>]*>/gi, '');
  1379→
  1380→    // Limpiar espacios múltiples y saltos de línea
  1381→    html = html.replace(/\s+/g, ' ');
  1382→    html = html.replace(/>\s+</g, '><');
  1383→
  1384→    return html.trim();
  1385→}
