  1387→/**
  1388→ * Ordena estudios por fecha_inicio más reciente (de más nueva a más antigua).
  1389→ * Preserva los índices originales para permitir edición y eliminación correcta.
  1390→ *
  1391→ * @param {Array} estudios - Array de objetos con estudios
  1392→ * @returns {Array} Array de estudios ordenados con propiedad indice_original
  1393→ * @author Jair Carvajal
  1394→ */
  1395→function ordenarEstudiosPorFecha(estudios) {
  1396→    // Mapear con índice original
  1397→    let estudios_con_indice = estudios.map((estudio, indice_original) => ({
  1398→        ...estudio,
  1399→        indice_original: indice_original
  1400→    }));
  1401→
  1402→    // Ordenar estudios por fecha_inicio (más reciente primero)
  1403→    let estudios_ordenados = estudios_con_indice.sort((a, b) => {
  1404→        // Si ambos tienen fecha_inicio, ordenar por más reciente
  1405→        if (a.fecha_inicio && b.fecha_inicio) {
  1406→            return moment(b.fecha_inicio).diff(moment(a.fecha_inicio));
  1407→        }
  1408→        // Si solo uno tiene fecha_inicio, ese va primero
  1409→        if (a.fecha_inicio && !b.fecha_inicio) return -1;
  1410→        if (!a.fecha_inicio && b.fecha_inicio) return 1;
  1411→        // Si ninguno tiene fecha_inicio, mantener orden original
  1412→        return 0;
  1413→    });
  1414→
  1415→    return estudios_ordenados;
  1416→}
  1417→
  1418→/**
  1419→ * Ordena experiencias laborales por fecha: primero las que está trabajando actualmente (sin fecha_fin),
  1420→ * luego por fecha_fin más reciente.
  1421→ * Preserva los índices originales para permitir edición y eliminación correcta.
  1422→ *
  1423→ * @param {Array} experiencias - Array de objetos con experiencias laborales
  1424→ * @returns {Array} Array de experiencias ordenadas con propiedad indice_original
  1425→ * @author Jair Carvajal
  1426→ */
  1427→function ordenarExperienciasPorFecha(experiencias) {
  1428→    // Mapear con índice original
  1429→    let experiencias_con_indice = experiencias.map((experiencia, indice_original) => ({
  1430→        ...experiencia,
  1431→        indice_original: indice_original
  1432→    }));
  1433→
  1434→    // Ordenar experiencias
  1435→    let experiencias_ordenadas = experiencias_con_indice.sort((a, b) => {
  1436→        // Si 'a' no tiene fecha_fin (trabaja actualmente), va primero
  1437→        if (!a.fecha_fin && b.fecha_fin) return -1;
  1438→        // Si 'b' no tiene fecha_fin (trabaja actualmente), va primero
  1439→        if (a.fecha_fin && !b.fecha_fin) return 1;
  1440→        // Si ambos están trabajando actualmente (sin fecha_fin), mantener el orden actual
  1441→        if (!a.fecha_fin && !b.fecha_fin) return 0;
  1442→        // Si ambos tienen fecha_fin, ordenar por fecha más reciente primero (descendente)
  1443→        return moment(b.fecha_fin).diff(moment(a.fecha_fin));
  1444→    });
  1445→
  1446→    return experiencias_ordenadas;
  1447→}
