  1449→/**
  1450→ * Obtener créditos disponibles de la empresa y actualizar el tag.
  1451→ * - Si tiene créditos >= 20% del plan: tag azul
  1452→ * - Si tiene créditos < 20% del plan: tag amarillo
  1453→ * - Si no tiene créditos: tag gris "Sin créditos"
  1454→ *
  1455→ * @param {number} empresa_id ID de la empresa
  1456→ * @param {string} contenedor_id ID del contenedor donde se mostrará el tag
  1457→ * @param {boolean} verificar_modal Si se debe verificar si mostrar el modal de advertencia
  1458→ * @returns {void}
  1459→ * @author Andres Gonzalez
  1460→ * @author Daniel Beltrán
  1461→ */
  1462→function obtenerCreditosDisponiblesEmpresa(empresa_id, contenedor_id = 'contenedor-tag-creditos', verificar_modal = false) {
  1463→    if (!empresa_id || !$(`#${contenedor_id}`).length) {
  1464→        return;
  1465→    }
  1466→
  1467→    $.ajax({
  1468→        url: `/empresas/${empresa_id}/creditos`,
  1469→        method: 'GET',
  1470→        dataType: 'json',
  1471→        success: function (respuesta) {
  1472→            if (respuesta?.data) {
  1473→                let creditos_disponibles = respuesta?.data?.creditos_disponibles ?? 0;
  1474→                let creditos_plan = respuesta?.data?.creditos_plan ?? 0;
  1475→                let plan_credito = respuesta?.data?.plan_credito;
  1476→                let porcentaje_disponible = creditos_plan > 0 ? (creditos_disponibles / creditos_plan) * 100 : 0;
  1477→
  1478→                let texto_tag = '';
  1479→                let clase_tag = '';
  1480→                let icono_final = '';
  1481→
  1482→                if (creditos_disponibles <= 0 || plan_credito === null) {
  1483→                    texto_tag = 'Sin créditos';
  1484→                    clase_tag = 'tag-tonal-gray';
  1485→                    icono_final = `<i class="bi bi-info-circle ms-1" role="button" tabindex="0" ${crearTooltip({ placement: 'right', content: 'No tienes créditos disponibles en este momento. Renueva tu plan o adquiere más créditos para continuar.' })}></i>`;
  1486→                } else if (porcentaje_disponible < 20) {
  1487→                    texto_tag = `Créditos disponibles: ${formatearNumero(creditos_disponibles)}`;
  1488→                    clase_tag = 'tag-filled-yellow';
  1489→                    icono_final = `<i class="bi bi-info-circle ms-1" role="button" tabindex="0" ${crearTooltip({ placement: 'right', content: 'Estás cerca de agotar tus créditos. Te queda menos del 20% del total disponible. Considera recargar o revisar tu consumo para evitar interrupciones.' })}></i>`;
  1490→                } else {
  1491→                    texto_tag = `Créditos disponibles: ${formatearNumero(creditos_disponibles)}`;
  1492→                    clase_tag = 'tag-tonal-blue';
  1493→                }
  1494→
  1495→                $(`#${contenedor_id}`).html(crearTag({
  1496→                    texto: texto_tag,
  1497→                    class: clase_tag,
  1498→                    icono_final: icono_final,
  1499→                }));
  1500→
  1501→                // Verificar si se debe mostrar el modal de advertencia
  1502→                if (verificar_modal && plan_credito !== null) {
  1503→                    verificarYMostrarModalAdvertenciaCreditosDisponibles(porcentaje_disponible, empresa_id);
  1504→                }
  1505→            } else {
  1506→                $(`#${contenedor_id}`).html(crearTag({
  1507→                    texto: 'No tienes plan de créditos',
  1508→                    class: 'tag-tonal-gray'
  1509→                }));
  1510→            }
  1511→
  1512→            inicializarTooltips();
  1513→        },
  1514→        error: function () {
  1515→            $(`#${contenedor_id}`).html(crearTag({
  1516→                texto: 'Error al cargar créditos',
  1517→                class: 'tag-tonal-gray'
  1518→            }));
  1519→        }
  1520→    });
  1521→}
  1522→
  1523→/**
  1524→ * Verificar si se debe mostrar el modal de advertencia de créditos disponibles.
  1525→ * Los umbrales son: 20%, 10%, 5% y 0%.
  1526→ * El modal solo se muestra una vez por día para cada umbral alcanzado.
  1527→ * Si baja a un umbral más bajo, se muestra ese nuevo umbral aunque ya se haya mostrado otro ese día.
  1528→ *
  1529→ * @param {number} porcentaje_disponible Porcentaje de créditos disponibles
  1530→ * @param {number} empresa_id ID de la empresa
  1531→ * @returns {void}
  1532→ * @author Sergio Vargas
  1533→ */
  1534→function verificarYMostrarModalAdvertenciaCreditosDisponibles(porcentaje_disponible, empresa_id) {
  1535→    // Determinar el umbral actual alcanzado (el más bajo que aplica)
  1536→    let umbral_actual = null;
  1537→
  1538→    for (let umbral of UMBRALES_ADVERTENCIA_CREDITOS) {
  1539→        if (porcentaje_disponible <= umbral) {
  1540→            umbral_actual = umbral;
  1541→        } else {
  1542→            break;
  1543→        }
  1544→    }
  1545→
  1546→    // Si no se alcanzó ningún umbral, no mostrar el modal
  1547→    if (umbral_actual === null) {
  1548→        return;
  1549→    }
  1550→
  1551→    // Obtener la fecha actual en formato YYYY-MM-DD
  1552→    let fecha_hoy = new Date().toISOString().split('T')[0];
  1553→    let llave_storage = `modal_creditos_${empresa_id}`;
  1554→
  1555→    // Obtener los datos guardados en localStorage
  1556→    let datos_guardados = localStorage.getItem(llave_storage);
  1557→    let umbrales_mostrados_hoy = {};
  1558→
  1559→    if (datos_guardados) {
  1560→        try {
  1561→            let datos = JSON.parse(datos_guardados);
  1562→            // Si la fecha guardada es de hoy, usar los umbrales guardados
  1563→            if (datos.fecha === fecha_hoy) {
  1564→                umbrales_mostrados_hoy = datos.umbrales || {};
  1565→            }
  1566→            // Si es de otro día, se resetean los umbrales (objeto vacío)
  1567→        } catch (e) {
  1568→            // Si hay error al parsear, se resetea
  1569→            umbrales_mostrados_hoy = {};
  1570→        }
  1571→    }
  1572→
  1573→    // Verificar si ya se mostró el modal para este umbral hoy
  1574→    if (umbrales_mostrados_hoy[umbral_actual]) {
  1575→        return;
  1576→    }
  1577→
  1578→    // Marcar este umbral como mostrado hoy
  1579→    umbrales_mostrados_hoy[umbral_actual] = true;
  1580→
  1581→    // Guardar en localStorage
  1582→    localStorage.setItem(llave_storage, JSON.stringify({
  1583→        fecha: fecha_hoy,
  1584→        umbrales: umbrales_mostrados_hoy
  1585→    }));
  1586→
  1587→    // Mostrar el modal
  1588→    mostrarModalAdvertenciaCreditosDisponibles(umbral_actual, empresa_id);
  1589→}
  1590→
  1591→/**
  1592→ * Mostrar el modal de advertencia de créditos disponibles.
  1593→ *
  1594→ * @param {number} umbral Umbral de porcentaje alcanzado (20, 10, 5 o 0)
  1595→ * @param {number} empresa_id ID de la empresa
  1596→ * @returns {void}
  1597→ * @author Sergio Vargas
  1598→ */
  1599→function mostrarModalAdvertenciaCreditosDisponibles(umbral, empresa_id) {
  1600→    let mensaje_descripcion = umbral === 0
  1601→        ? 'Tu saldo de créditos se ha agotado. Solicita más créditos ahora para evitar interrupciones en tus acciones.'
  1602→        : `Tu saldo está por debajo del ${umbral}% de créditos disponibles. Solicita más créditos ahora para evitar interrupciones en tus acciones.`;
  1603→
  1604→    Swal.fire({
  1605→        showCloseButton: true,
  1606→        closeButtonHtml: '<i class="bi bi-x text-headline-4 text-gray-20"></i>',
  1607→        showConfirmButton: false,
  1608→        showDenyButton: false,
  1609→        showCancelButton: false,
  1610→        html: `
  1611→            <div class="row w-100 mx-0 mt-2">
  1612→                <div class="col-12 fw-semibold text-start text-gray-10 text-headline-4">Solicita más créditos</div>
  1613→                <div class="col-12 mt-4 text-start text-body-2 fw-normal text-gray-30">${mensaje_descripcion}</div>
  1614→                <div class="col-6 mt-5 mb-2">
  1615→                    ${crearBoton({
  1616→                        clases: 'btn-outline-primary w-100 justify-content-center',
  1617→                        texto: 'Cancelar',
  1618→                        onclick: `Swal.close();`
  1619→                    })}
  1620→                </div>
  1621→                <div class="col-6 mt-5 mb-2">
  1622→                    ${crearBoton({
  1623→                        clases: 'btn-primary w-100 justify-content-center',
  1624→                        texto: 'Solicitar más créditos',
  1625→                        onclick: `solicitarMasCreditosDesdeModal(${empresa_id});`
  1626→                    })}
  1627→                </div>
  1628→            </div>
  1629→        `,
  1630→    });
  1631→}
  1632→
  1633→/**
  1634→ * Solicitar más créditos desde el modal de advertencia.
  1635→ * Contacta al ejecutivo de la empresa via WhatsApp.
  1636→ *
  1637→ * @param {number} empresa_id ID de la empresa
  1638→ * @returns {void}
  1639→ * @author Sergio Vargas
  1640→ */
  1641→function solicitarMasCreditosDesdeModal(empresa_id) {
  1642→    $.ajax({
  1643→        url: `/empresas/${empresa_id}`,
  1644→        method: 'GET',
  1645→        dataType: 'json',
  1646→        success: function(respuesta) {
  1647→            if (respuesta?.data?.ejecutivo?.telefono) {
  1648→                window.open(`https://wa.me/${respuesta.data.ejecutivo.telefono.replace(/\D/g, '')}?text=Hola,%20me%20gustar%C3%ADa%20solicitar%20cr%C3%A9ditos%20adicionales%20para%20mi%20empresa.`, '_blank');
  1649→            } else {
  1650→                mostrarToast('danger', 'No se encontró un ejecutivo asignado a la empresa. Por favor, comunícate con soporte para recargar créditos.');
  1651→            }
  1652→            Swal.close();
  1653→        },
  1654→        error: function() {
  1655→            mostrarToast('danger', 'Ocurrió un error al obtener la información del ejecutivo.');
  1656→            Swal.close();
  1657→        }
  1658→    });
  1659→}
  1660→
  1661→/**
  1662→ * Obtiene las funcionalidades activas del plan vigente de la empresa del usuario actual.
  1663→ *
  1664→ * @param {number|null} tipo_prueba_id ID del tipo de prueba (opcional).
  1665→ * @returns {Promise} Promesa que se resuelve con las funcionalidades activas.
  1666→ * @author Andres Gonzalez
  1667→ */
  1668→function obtenerFuncionalidadesActivasPlan(tipo_prueba_id = null) {
  1669→    let parametros = {};
  1670→
  1671→    if (tipo_prueba_id !== null) {
  1672→        parametros.tipo_prueba_id = tipo_prueba_id;
  1673→    }
  1674→
  1675→    return $.ajax({
  1676→        url: '/funcionalidades-activas-plan',
  1677→        method: 'GET',
  1678→        dataType: 'json',
  1679→        data: parametros,
  1680→        success: function(respuesta) {
  1681→            funcionalidades_disponibles_empresa = respuesta?.data ?? [];
  1682→        }
  1683→    });
  1684→}
  1685→
  1686→/**
  1687→ * Muestra Modal para informar que no cuenta con la funcionalidad
  1688→ *.
  1689→ * @returns {void}
  1690→ * @author Andres Gonzalez
  1691→ */
  1692→function modalFuncionalidadInactiva() {
  1693→    Swal.fire({
  1694→        showCloseButton: true,
  1695→        closeButtonHtml: '<i class="bi bi-x text-headline-4 text-gray-20"></i>',
  1696→        showConfirmButton: false,
  1697→        showDenyButton: false,
  1698→        showCancelButton: false,
  1699→        html: `
  1700→            <div class="row w-100 mx-0 mt-2">
  1701→                <div class="col-12 fw-semibold text-start text-gray-10 text-headline-4">Adquiere esta funcionalidad</div>
  1702→                <div class="col-12 mt-5 text-start text-body-2 fw-normal">No tienes esta funcionalidad activa. Por favor, contacta a tu asesor.</div>
  1703→                <div class="col-6 mt-5 mb-2">
  1704→                    ${crearBoton({
  1705→                        clases: 'btn-outline-primary w-100 justify-content-center',
  1706→                        texto: 'Cancelar',
  1707→                        onclick: `Swal.clickDeny();`
  1708→                    })}
  1709→                </div>
  1710→                <div class="col-6 mt-5 mb-2">
  1711→                    ${crearBoton({
  1712→                        clases: 'btn-primary w-100 justify-content-center',
  1713→                        texto: 'Entiendo',
  1714→                        onclick: `Swal.clickConfirm();`
  1715→                    })}
  1716→                </div>
  1717→            </div>
  1718→        `,
  1719→    }).then(async (resultado) => {
  1720→        //
  1721→    });
  1722→}
  1723→
  1724→/**
  1725→ * Busca una funcionalidad por su llave.
  1726→ *
  1727→ * @param {string} llave Llave de la funcionalidad a buscar.
  1728→ * @param {Array|null} funcionalidades funcionalidades.
  1729→ * @returns {boolean}
  1730→ * @author Andres Gonzalez
  1731→ */
  1732→function buscarFuncionalidad(llave, funcionalidades = null) {
  1733→    let datos = funcionalidades ?? funcionalidades_disponibles_empresa;
  1734→
  1735→    if (!Array.isArray(datos) || datos.length === 0) {
  1736→        return false;
  1737→    }
  1738→
  1739→    return datos.some(item => item?.funcionalidad?.llave === llave);
  1740→}
  1741→
  1742→/**
  1743→ * Obtener el límite de consumo de alguna característica de un plan.
  1744→ *
  1745→ * Si el plan es ilimitado (que tiene fecha de vencimiento), entonces, se asigna un guión como indicativo.
  1746→ *
  1747→ * @param {Object} datos Datos del plan de la empresa.
  1748→ * @param {string} campo_limite Campo que establece el límite de consumo.
  1749→ * @returns {number|string}
  1750→ * @author Daniel Beltrán
  1751→ */
  1752→function obtenerLimiteConsumo(datos, campo_limite) {
  1753→    let limite_unidades = datos[campo_limite] ?? 0;
  1754→
  1755→    if (datos.fecha_vencimiento != null) {
  1756→        limite_unidades = '-';
  1757→    }
  1758→
  1759→    return limite_unidades;
  1760→}
