// Mensajes en español para el plugin Select2
const mensajes_select2 = {
    'noResults'              : () => { return 'No hay resultados para la búsqueda.'; },
    'searching'              : () => { return 'Buscando...'; },
    'Loading'                : () => { return 'Cargando...'; },
    'loadingMore'            : () => { return 'Cargando más resultados...'; },
    'dropdownFooter'         : (texto) => { return `Mostrando ${texto} resultados`; },
    'emptyOptions'           : () => { return 'No hay opciones disponibles.'; },
    'appendItems'            : (items) => { return `Agregando ${items.length} elementos.`; },
    'maximumSelectionReached': () => { return 'Se ha alcanzado el número máximo de selecciones.'; },
    'selectOnClose'          : () => { return 'Seleccione una opción al cerrar el menú.'; },
    'escapeKeyUp'            : () => { return 'Presione \'Escape\' para cerrar el menú.'; },
    'errorLoading'           : () => { return 'Error al cargar datos. Inténtalo de nuevo más tarde.'; },
    'inputTooShort'          : (elemento) => { return `Ingresa al menos ${elemento.minimum} caracteres para iniciar una búsqueda.`; },
    'noMatches'              : () => { return 'No se encontraron coincidencias para tu búsqueda.'; },
    'allowClear'             : () => { return 'Borrar selección'; },
    'SelectionTooLong'       : (conteo, maximo) => { return `Solo puedes seleccionar ${maximo} opciones.`; },
    'loadMore'               : () => { return 'Cargando más resultados...'; },
    'removing'               : (seleccion) => { return 'Eliminando...'; },
    'matches'                : (coincidencias, total) => {
        if (coincidencias === 0) {
            return 'No hay coincidencias.';
        } else if (coincidencias === 1) {
            return 'Se encontró 1 coincidencia.';
        } else {
            return `Se encontraron ${coincidencias} coincidencias entre ${total}.`;
        }
    }
};

// audios
var audio_notificacion = new Audio('/audios/notificacion.mp3');

let mostro_modal_creditos_ia = false;
let funcion_ejecucion_ia = null;
let modals_abiertos_ia = null;

let funcionalidades_disponibles_empresa = [];

// Umbrales para mostrar el modal de advertencia de créditos (en porcentaje)
const UMBRALES_ADVERTENCIA_CREDITOS = [20, 10, 5, 0];

// Constantes de rangos de las adecuaciones de vacantes
const ADECUACION_CALIFICACION_BAJA = 40;
const ADECUACION_CALIFICACION_ALTA = 70;

/**
 * Mostrar modal de créditos IA.
 *
 * @return {void}
 * @author José Manuel Jiménez
 * @author Sergio Vargas
 * @author Daniel Beltrán
 */
function mostrarModalCreditosIA(servicio_ia_id) {
    // Verificar si el usuario optó por no mostrar el modal
    if (localStorage.getItem('no_mostrar_modal_creditos_ia') === 'true') {
        mostro_modal_creditos_ia = true;
        if (funcion_ejecucion_ia) {
            funcion_ejecucion_ia();
            funcion_ejecucion_ia = null;
        }
        mostro_modal_creditos_ia = false;
        return;
    }

    $.ajax({
        url     : `/creditos-disponibles-empresas/${$('#empresa_sesion_id').val()}`,
        method  : 'GET',
        dataType: 'json',
        success : function (respuesta_empresa) {
            if (respuesta_empresa.data?.creditos > 0) {
                $.ajax({
                    url     : `/servicios-ia/${servicio_ia_id}/creditos`,
                    method  : 'GET',
                    dataType: 'json',
                    success : function (respuesta) {
                        if (respuesta?.data?.consume_creditos && respuesta?.data?.consume_creditos <= respuesta_empresa.data.creditos) {
                            $('#cantidad-creditos-descontar-modal').text(respuesta.data.consume_creditos);
                            $('#modal-consumo-creditos-ia').modal('show');
                            mostro_modal_creditos_ia = true;
                            modals_abiertos_ia = $('.modal.show').not('#modal-consumo-creditos-ia').not('#modal-generando-ia');
                            $(modals_abiertos_ia).addClass('d-none');
                        } else {
                            mostrarToastSolicitarCreditosIA();
                        }
                    }
                });
            } else {
                mostrarToastSolicitarCreditosIA();
            }
        }
    });
}

/**
 * Mostrar toast para solicitar créditos IA
 *
 * @return void
 * @author José Manuel Jiménez
 */
function mostrarToastSolicitarCreditosIA() {
    $.ajax({
        url: `/empresas/${$('#empresa_sesion_id').val()}`,
        method: 'GET',
        dataType: 'json',
        success: function(respuesta) {
            let adicional_toast = '';
            if (respuesta?.data?.ejecutivo?.telefono) {
                adicional_toast = `<a href="https://wa.me/${respuesta.data.ejecutivo.telefono.replace(/\D/g, '')}?text=Hola,%20me%20gustar%C3%ADa%20solicitar%20cr%C3%A9ditos%20adicionales%20para%20mi%20empresa." target="_blank">
                    <p class="text-label-1 text-white no-visited text-decoration-none mb-0">Solicitar</p>
                </a>`;
            }
            setTimeout(() => {
                $('#modal-generando-ia').modal('hide');
                $('#modal-cargue-ia').modal('hide');
                mostrarToast('danger', 'No cuentas con créditos. Solicita créditos adicionales.', '', adicional_toast);
            }, 300);
        }
    });
}

/**
 * Continuar ejecución IA luego del modal de créditos
 *
 * @return void
 * @author José Manuel Jiménez
 * @author Sergio Vargas
 */
function continuarEjecucionIA() {
    // Guardar preferencia si el checkbox está marcado
    if ($('#no_mostrar_modal_creditos_ia').is(':checked')) {
        localStorage.setItem('no_mostrar_modal_creditos_ia', 'true');
    }

    if (funcion_ejecucion_ia) {
        funcion_ejecucion_ia();
        funcion_ejecucion_ia = null;
    }
    mostro_modal_creditos_ia = false;
    $('#modal-consumo-creditos-ia').modal('hide');
}

/**
 * Solicitar más créditos IA
 *
 * @return void
 * @author José Manuel Jiménez
 */
function solicitarMasCreditosIA() {
    $.ajax({
        url: `/empresas/${$('#empresa_sesion_id').val()}`,
        method: 'GET',
        dataType: 'json',
        success: function(respuesta) {
            if (respuesta?.data?.ejecutivo?.telefono) {
                window.open(`https://wa.me/${respuesta.data.ejecutivo.telefono.replace(/\D/g, '')}?text=Hola,%20me%20gustar%C3%ADa%20solicitar%20cr%C3%A9ditos%20adicionales%20para%20mi%20empresa.`, '_blank');
            } else {
                mostrarToast('danger', 'No se encontró un ejecutivo asignado a la empresa. Por favor, comunícate con soporte para recargar créditos.');
            }
            $('#modal-solicitar-mas-creditos-ia').modal('hide');
        }
    });
}

/**
 * Validar respuesta de créditos IA
 *
 * @return void
 * @author José Manuel Jiménez
 */
function validarRespuestaCreditosIA(error_ia) {
    if (error_ia == 'No tienes créditos disponibles en este momento. Renueva tu plan o adquiere más créditos para continuar.') {
        mostrarToastSolicitarCreditosIA();
        return true;
    }
    return false;
}

/**
 * Ajustar el ancho de los elementos select2 asincrono multiple.
 *
 * @returns {void}
 * @author Andres Morales
 */
$(document).ready(function () {
    cargarNotificiacionesHeader();
    if (window.configuracion_reverbg && window.configuracion_reverbg.activar_websocket) {
        configurarWebSocketNotificaciones();
    }
    setTimeout(() => {
        $(".input-container").each(function () {
            let $container = $(this);
            let $select = $container.find("select[multiple]");
            let $icon = $container.find(".input-icono1");
            if ($select.length && $icon.length) {
                $(this).find('.select2-selection--multiple').addClass('ps-5')
            }
        });
    }, 1000);
    $('#modal-consumo-creditos-ia').on('hidden.bs.modal', function () {
        if (mostro_modal_creditos_ia) {
            $(modals_abiertos_ia).removeClass('d-none');
        }
        mostro_modal_creditos_ia = false;
    });

    // Cargar créditos disponibles si existe el contenedor y el ID de empresa
    if ($('#contenedor-tag-creditos').length && $('#empresa_sesion_id').length) {
        // Verificar si estamos en la ruta /procesos para mostrar el modal de advertencia
        let verificar_modal = window.location.pathname === '/procesos';
        obtenerCreditosDisponiblesEmpresa($('#empresa_sesion_id').val(), 'contenedor-tag-creditos', verificar_modal);
    }
});

/**
 * Convertir fecha de formato YYYY-MM-DD a DD-MM-YYYY.
 *
 * @param {string} fecha Fecha en formato ISO (YYYY-MM-DD).
 * @returns {string} Fecha convertida a formato DD-MM-YYYY.
 * @author Andres Morales
 * @author Sergio Vargas
 */
const convertirFecha = fecha => {
    if (!fecha) return '';
    fecha = String(fecha).substring(0, 10);
    return fecha.includes('-') ? fecha.split('-').reverse().join('-') : 'Formato incorrecto';
};

/**
 * Formatear fecha a formato dd/mm/yyyy - hh:mm AM/PM.  Ejemplo: 01/Feb/2022 - 12:00 PM
 *
 * @param {string} fecha_entrada fecha en formato ISO
 * @returns {string} fecha y hora formateada
 * @author Andres Morales
 */
function formatearFecha(fecha_entrada) {
    var fecha = new Date(fecha_entrada);
    var dia = fecha.getDate();
    var mes = fecha.toLocaleString('es-ES', { month: 'short' });
    var anio = fecha.getFullYear();
    let hora = fecha.getHours();
    let minuto = fecha.getMinutes();
    let ampm = hora >= 12 ? 'PM' : 'AM';
    hora = hora % 12 || 12;
    minuto = minuto.toString().padStart(2, '0');
    return `${dia}/${mes}/${anio} - ${hora}:${minuto} ${ampm}`;
}

function formatearFecha2(fecha_entrada) {
    let partes = fecha_entrada.split(' ');
    let fecha = partes[0].split('-');
    let hora = partes[1];
    let fechaISO = `${fecha[2]}-${fecha[1]}-${fecha[0]}T${hora}`;

    let date = new Date(fechaISO);

    if (isNaN(date.getTime())) {
        return 'Fecha inválida';
    }

    let dia = date.getDate();
    let mes = date.toLocaleString('eu-EU', { month: 'short' });
    let anio = date.getFullYear();
    return `${dia}/${mes}/${anio}`;
}

