/**
 * Funcion para hacer el slug de un texto desde jquery.
 *
 * @returns {void}
 * @author Andres Morales
 * @deprecated No utilizar para cargar logos de pruebas, usar el slug ligado a la prueba en su lugar.
 */
function slugify(text) {
    return $.trim(text)
        .toLowerCase()
        .normalize("NFD")
        .replace(/[\u0300-\u036f]/g, "")
        .replace(/[^a-z0-9 -]/g, "")
        .replace(/\s+/g, "-")
        .replace(/-+/g, "-");
}

/**
 * Función generica para mostrar mensajes de respuesta
 *
 * @param {string} tipo_alerta define el color de la alerta segun alerts de botstrap.
 * @param {string} texto_1 Texto que mostrara la alerta.
 * @param {string} texto_2 Texto secundario en la alerta.
 * @param {string} accion Acción adicional(por definir).
 * @returns {void}
 * @author José Manuel Jiménez
 * @author Andres Gonzalez
 */
function mostrarToast(tipo_alerta, texto_1, texto_2 = '', accion = '', icono2 = '') {
    let icono = '';
    let texto_color = 'text-white';
    if (accion != '') {
        accion = `<div class="col-auto p-0 text-label-1">${accion}</div>`;
    }

    if (tipo_alerta == 'success') {
        icono = 'bi bi-check-circle';
    }

    if (tipo_alerta == 'neutral') {
        icono = 'bi bi-info-circle';
    }

    if (tipo_alerta == 'warning') {
        icono = 'bi bi-info-circle';
        texto_color = "text-red-letras";
    }

    if (tipo_alerta == 'danger') {
        icono = 'bi bi-x-circle';
    }

    if (icono2 != '') {
        icono = icono2;
    }

    if (texto_2 != '') {
        texto_2 = `<div class="col-12 p-0 text-label-1 ${texto_color}">${texto_2}</div>`;
    }

    let alert_toast = $(`
        <div class="alert toast-${tipo_alerta} alert-dismissible fade show d-flex align-items-center pe-3 py-3 me-3" role="alert" style="display:none">
            <div class="col-auto">
                <span class="${icono} ${texto_color}"></span>
            </div>
            <div class="col px-3">
                <div class="row m-0 ${texto_color}">
                    <div class="col-12 p-0 text-label-1 ${texto_color}">
                        ${texto_1}
                    </div>
                    ${texto_2}
                </div>
            </div>
            ${accion}
            <div class="col-auto ms-3 pointer" data-bs-dismiss="alert">
                <span class="bi bi-x ${texto_color}"></span>
            </div>
        </div>
    `);
    $('#contenedor-alert-toast').append(alert_toast);
    alert_toast.fadeIn();
    setTimeout(function() {
        alert_toast.fadeOut();
        alert_toast.alert('close');
    }, 5000);
}

/**
 * Formatear número.
 *
 * @param {number} numero número a formatear
 * @author Andres Morales
 */
function formatearNumero(numero) {
    return parseInt(numero).toLocaleString('es-ES');
}

/**
 * Formatear fecha.
 *
 * @param {*} fecha Fecha a formatear.
 * @param {boolean} incluir_segundos Indica si se debe incluir los segundos en la hora.
 * @param {string} formato Formato en el que se retornará la fecha.
 * @param {string} separador Símbolo que se usará para separar días, meses y años.
 * @returns {Object} Objeto con la fecha y hora formateada.
 * @author Daniel Beltrán
 */
function formatearFechaConceptual(fecha, incluir_segundos = false, formato = 'Ymd', separador = '-') {
    const NUEVA_FECHA = new Date(fecha);
    const ANO         = NUEVA_FECHA.getFullYear();
    const MES         = String(NUEVA_FECHA.getMonth() + 1).padStart(2, '0');
    const DIA         = String(NUEVA_FECHA.getDate()).padStart(2, '0');
    const HORAS       = String(NUEVA_FECHA.getHours()).padStart(2, '0');
    const MINUTOS     = String(NUEVA_FECHA.getMinutes()).padStart(2, '0');
    const SEGUNDOS    = String(NUEVA_FECHA.getSeconds()).padStart(2, '0');

    let fecha_formateada = `${ANO}${separador}${MES}${separador}${DIA}`;

    if (formato === 'dmY') {
        fecha_formateada = `${DIA}${separador}${MES}${separador}${ANO}`;
    }

    return {
        fecha: fecha_formateada,
        hora : incluir_segundos ? `${HORAS}:${MINUTOS}:${SEGUNDOS}` : `${HORAS}:${MINUTOS}`
    };
}

/**
 * Formatear fecha a May. 2023.
 *
 * @param {string} fechaISO Fecha a formatear.
 * @returns {string} Objeto con la fecha y hora formateada.
 * @author Andres Morales
 */
function formatearFechaAnioMes(fechaISO) {
    const fecha = new Date(fechaISO);
    const opciones = { month: 'short', year: 'numeric' };
    let fecha_formateada = fecha.toLocaleDateString('es-ES', opciones);

    fecha_formateada = fecha_formateada.replace(
        /^([a-zñ]+)(\s\d{4})$/,
        (_, mes, anio) => `${mes.charAt(0).toUpperCase() + mes.slice(1)}. ${anio.trim()}`
    );

    return fecha_formateada;
}

/**
 * Formatear un numero a moneda.
 *
 * @param {string} valor Numero.
 * @returns {string} Numero formateado.
 * @author Andres Morales
 */
function formatearMonedaCOP(valor) {
    return new Intl.NumberFormat('es-CO', {
        style: 'currency',
        currency: 'COP',
        minimumFractionDigits: 0,
        maximumFractionDigits: 0
    }).format(valor);
}

/**
 * Formatear fecha a un formato d/m/Y hh:mm:ss entendible para el usuario.
 *
 * @param {*} fecha_inicial Fecha a formatear.
 * @returns {String} String con la fecha y hora formateada.
 * @author José Manuel Jiménez
 */
function fechaMostrar(fecha_inicial) {
    if (!fecha_inicial) {
        return "";
    }
    const fecha = new Date(fecha_inicial);
    if (isNaN(fecha.getTime())) {
        return "";
    }
    const dia = String(fecha.getUTCDate()).padStart(2, '0');
    const mes = String(fecha.getUTCMonth() + 1).padStart(2, '0');
    const anio = fecha.getUTCFullYear();
    // Verificar si el string incluye información de hora (espacio, T, o dos puntos)
    if (!fecha_inicial.includes(" ") && !fecha_inicial.includes("T") && !fecha_inicial.includes(":")) {
        return `${dia}/${mes}/${anio}`;
    }
    const horas = String(fecha.getUTCHours()).padStart(2, '0');
    const minutos = String(fecha.getUTCMinutes()).padStart(2, '0');
    const segundos = String(fecha.getUTCSeconds()).padStart(2, '0');
    return `${dia}/${mes}/${anio} ${horas}:${minutos}:${segundos}`;
}

/**
 * Descargar Excel.
 *
 * @param {string} ruta Ruta desde donde se descarga el archivo.
 * @param {string} nombre_archivo Nombre del archivo descargado.
 * @param {boolean} marca_tiempo Indica si el nombre del archivo debe llevar marca de tiempo.
 * @param {Object} parametros Parámetros a enviar en la petición.
 * @returns {void}
 * @author Daniel Beltrán
 */
async function descargarExcel(ruta, nombre_archivo, marca_tiempo = true, parametros = {}) {
    parametros._token = $('meta[name="csrf-token"]').attr('content');

    await $.ajax({
        type     : 'POST',
        url      : `/${ruta}`,
        data     : parametros,
        xhrFields: {
            responseType: 'blob'
        },
        success: function (respuesta) {
            // Crear una URL para el Blob
            let url = window.URL.createObjectURL(respuesta); // 'respuesta' es el Blob
            let a   = document.createElement('a'); // Crea un nuevo elemento <a> para iniciar la descarga
            a.href  = url;
            const fecha_actual     = new Date();
            const dia              = fecha_actual.getDate();
            const mes              = fecha_actual.getMonth() + 1;
            const ano              = fecha_actual.getFullYear();
            const hora             = fecha_actual.getHours();
            const minutos          = fecha_actual.getMinutes();
            const segundos         = fecha_actual.getSeconds();
            const fecha_formateada = `${dia}${mes}${ano}${hora}${minutos}${segundos}`;

            a.download = marca_tiempo ? `${nombre_archivo}-${fecha_formateada}.xlsx` : `${nombre_archivo}.xlsx`;
            document.body.appendChild(a);
            a.click();
            window.URL.revokeObjectURL(url);
            document.body.removeChild(a);
        }
    }).then(() => {
        mostrarToast('success', 'Archivo descargado correctamente.');
    }).catch((error) => {
        mostrarToast('danger', 'Ocurrió un error al descargar el archivo.');
    });
}

/**
 * Convierte un string a slug.
 *
 * @param {string} str
 * @returns {string}
 * @author Fahibram Cárcamo
 */
function slug(str) {

    // Quita espacios en blanco
    str = str.replace(/^\s+|\s+$/g, "")

    // Convierte el string a minusculas
    str = str.toLowerCase()

    // Quita los caracteres no alfanuméricos
    str = str.replace(/[^a-z0-9 -]/g, "")

    // Reemplaza los espacio con guiones
    str = str.replace(/\s+/g, "-")

    // Remueve los guiones consecutivos
    str = str.replace(/-+/g, "-")

    return str
}

/**
 * Compara los elementos de dos arreglos y retorna `true` si son iguales y `false` en caso contrario
 * (Este función está pensada para comparar arreglos unidimensionales).
 *
 * @param {array} primer_arreglo
 * @param {array} segundo_arreglo
 * @param {boolean} comparar_orden
 * @return {boolean}
 * @author Fahibram Cárcamo
 */
function sonArreglosIguales(primer_arreglo, segundo_arreglo, comparar_orden = false) {

    if (!comparar_orden) {
        primer_arreglo.sort()
        segundo_arreglo.sort()
    }

    if (primer_arreglo.length !== segundo_arreglo.length) return false

    for (let i = 0; i < primer_arreglo.length; i++) {
        if (primer_arreglo[i] !== segundo_arreglo[i]) return false
    }

    return true
}

