        success: function(respuesta) {
            let datos = respuesta.data;
            let notificaciones_noleidas = datos?.unread_notifications?.length ?? 0;

            // Siempre remover el badge existente
            $('#dropdownNotificaciones #badge-notificaciones').remove();

            // Solo agregar el badge si hay notificaciones no leídas
            if (notificaciones_noleidas > 0) {
                $('#dropdownNotificaciones').append(`<span id="badge-notificaciones" class="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-state-red border border-white">${notificaciones_noleidas}</span>`);
                $('#contador-notificaciones-abierto').append(`<span id="badge-notificaciones" class="badge rounded-pill bg-state-red border border-white">${notificaciones_noleidas}</span>`);

                $('#contenedor-boton-marcar-todas').html(`
                    <button class="btn btn-link text-primary w-100 d-flex justify-content-center" onclick="marcarNotificacionesLeidas('all')">
                        Marcar todo como leído
                    </button>
                `);
            }

            const NO_LEIDOS_IDS = new Set(Array.isArray(datos?.unread_notifications) ? datos.unread_notifications.map(notificacion => notificacion.id) : []);

            if (Array.isArray(datos?.notifications) && datos.notifications.length > 0) {
                $('#notificacion-sinnotificaciones').hide();
                $('.notificaciones-listado').remove();
                datos.notifications.forEach(notificacion => {
                    const { id, data, updated_at } = notificacion;

                    const NO_LEIDO = NO_LEIDOS_IDS.has(id);
                    const MENSAJE = (data?.mensaje || '').slice(0, 120) + '...';
                    const HORA = new Date(updated_at).toLocaleTimeString('es-CO', {
                        hour: '2-digit',
                        minute: '2-digit',
                        hour12: false
                    });

                    const notificaciones = `
                        <li class="${NO_LEIDO ? '' : 'bg-gray-90'} notificaciones-listado py-0" id="notificacion-${id}">
                            <a class="dropdown-item text-gray-10 text-decoration-none border-top pt-0 pb-1" href="javascript:void(0)" onclick="marcarNotificacionesLeidas('${id}','${data?.ruta}')">
                                <div class="d-flex justify-content-between">
                                    <div class="w-auto flex-grow-1 pe-3">
                                        <p class="mt-1 mb-0 text-headline-6 text-gray-10">${data?.titulo}</p>
                                        <p class="mb-0 text-body-3 text-gray-30 notificacion-mensaje">${MENSAJE}</p>
                                    </div>
                                    <div class="w-auto my-auto ${NO_LEIDO ? 'text-cta-azul' : 'text-gray-10'} text-label-2 flex-shrink-0">
                                        ${HORA}${NO_LEIDO ? `<i class="bi bi-circle-fill text-cta-azul my-auto texto-notificacion-icono ms-3"></i>` : ''}
                                    </div>
                                </div>
                            </a>
                        </li>
                    `;

                    $(notificaciones).insertBefore('#contenedor-boton-marcar-todas');
                });

            } else {
                $('#notificacion-sinnotificaciones').hide();
                $('.notificaciones-listado').remove();
            }

        },
        error: function(xhr, status, error) {
            console.error('Error al cargar notificaciones:', error);
        }
    });
}

/**
 * Marcar las notificaciones leídas.
 *
 * @param {array} notificaciones_ids ids de las notificaciones
 * @param {string} ruta_destino ruta donde se va ha actualizar la pagina
 * @returns {void}
 * @author Andres Gonzalez
 * @author Esperanza Olivo
 */
function marcarNotificacionesLeidas(notificaciones_ids = null, ruta_destino = null, soporte_id = null) {

    let data = {
        _token: $('meta[name="csrf-token"]').attr('content')
    };

    if (notificaciones_ids === 'all') {
        data.marcar_todo = true;
    } else if (soporte_id) {
        data.soporte_id = soporte_id;
    } else if (notificaciones_ids) {
        data.notificaciones_ids = Array.isArray(notificaciones_ids)
            ? notificaciones_ids
            : [notificaciones_ids];
    }

    $.ajax({
        url: '/usuario-marcar-notificaciones-leidas',
        type: 'POST',
        data: data,
        success: function(respuesta) {
            cargarNotificiacionesHeader();

            if (notificaciones_ids && notificaciones_ids !== 'all' && ruta_destino) {
                window.location.href = "/" + ruta_destino;
            }

            if (notificaciones_ids === 'all') {
                $('#badge-notificaciones').remove();
                $('#contenedor-boton-marcar-todas').empty();
                $('.notificaciones-listado').addClass('bg-gray-90');
                $('.bi-circle-fill').remove();
            }
        },
        error: function(xhr, estatus, error) {
            console.error('Error al cargar notificaciones:', error);
        }
    });
}

/**
 * Configurar WebSocket para notificaciones en tiempo real
 *
 * @return void
 * @author Andres Gonzalez
 */
function configurarWebSocketNotificaciones() {
    try {
        if (!window.configuracion_reverbg) {
            return;
        }

        const PUSHER = new Pusher(window.configuracion_reverbg.appKey, {
            wsHost: window.configuracion_reverbg.host,
            wsPort: window.configuracion_reverbg.port,
            wssPort: window.configuracion_reverbg.port,
            cluster: 'mt1',
            forceTLS: false,
            enabledTransports: ['ws', 'wss'],
            authEndpoint: '/broadcasting-auth',
            auth: {
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                }
            }
        });

        PUSHER.connection.bind('failed', function() {
            console.error('❌ Conexión WebSocket falló');
        });

        PUSHER.connection.bind('error', function(error) {
            console.error('❌ Error de WebSocket:', error);
        });

        let usuario_id = window.configuracion_reverbg.usuario_id;

        if (!usuario_id) {
            return;
        }

        const NOMBRE_CANAL = `private-App.Models.Usuarios.User.${usuario_id}`;
        const CANAL = PUSHER.subscribe(NOMBRE_CANAL);

        CANAL.bind('pusher:subscription_error', function(error) {
            console.error('❌ Error al suscribirse al canal:', error);
        });

        // Escuchar eventos específicos de notificaciones
        CANAL.bind('Illuminate\\Notifications\\Events\\BroadcastNotificationCreated', function(data) {
            if (data.tipo === 'nueva_notificacion') {
                audio_notificacion.play();
                cargarNotificiacionesHeader();
            }

            if (data.tipo === 'nuevo_mensaje') {
                if (($('#soporte-guardado-id').val() == data.soporte_id) || ($('#soporte_empresa_id').val() == data.soporte_id)) {
                    audio_notificacion.play();
                    consultarChat(data.soporte_id);
                }
            }
        });
    } catch (error) {
        console.error('💥 Error en configurarWebSocketNotificaciones:', error);
        console.error('Stack trace:', error.stack);
    }
}

/**
 * Retornar el string entrante en formato title case.
 *
 * @param {string} texto
 * @returns {string}
 * @author Fahibram Cárcamo
 */
function titleCase(texto) {
    return texto.toLowerCase().replace(/\b\w/g, caracter => caracter.toUpperCase());
}

/**
 * Número de horas que durará el almacenamiento de la variable `ulitmas_empresas` en el
 * almacenamiento local **LocalStorage**.
 *
 * @var {number}
 * @author Fahibram Cárcamo
 */
const CANTIDAD_HORAS_VENCIMIENTO_ULTIMAS_EMPRESAS_LOCALSTORAGE = 6;

/**
 * Validar si ya debe removerse la variable `ultimas_empresas` del alamacenamiento local
 * **LocalStorage**, si no es tiempo de eliminarla entonces se reinicia el tiempo de vigencia de la
 * variable.
 *
 * @returns {void}
 * @author Fahibram Cárcamo
 */
function validarYActualizarUltimasEmpresasLocalStorage() {
    let fecha_hora_actuales = moment();
    let fecha_hora_vencimiento_ultimas_empresas = fecha_hora_actuales.clone().add(CANTIDAD_HORAS_VENCIMIENTO_ULTIMAS_EMPRESAS_LOCALSTORAGE, 'hours');
    let fecha_hora_vencimiento_ultimas_empresas_localstorage = localStorage.getItem('vencimiento_ultimas_empresas');
    let ultimas_empresas = localStorage.getItem('ultimas_empresas');

    if (
        ultimas_empresas != null &&
        fecha_hora_vencimiento_ultimas_empresas_localstorage != null &&
        fecha_hora_actuales.clone().isBefore(moment(fecha_hora_vencimiento_ultimas_empresas_localstorage).clone())
    ) {
        localStorage.setItem('vencimiento_ultimas_empresas', fecha_hora_vencimiento_ultimas_empresas.format('YYYY-MM-DD HH:mm:ss'));
        return;
    }

    localStorage.removeItem('vencimiento_ultimas_empresas');
    localStorage.removeItem('ultimas_empresas');
}

/**
 * Obtener la edad actual de un usuario según su fecha de nacimiento.
 *
 * @param {string} fecha_nacimiento
 * @returns {string}
 * @author José Manuel Jiménez
 * @author Fahibram Cárcamo
 * @author Andres Morales
 */
function obtenerEdad(fecha_nacimiento) {
    if (!fecha_nacimiento) return "N/A";
    let hoy = new Date();
    let nacimiento = new Date(fecha_nacimiento);
    let edad = hoy.getFullYear() - nacimiento.getFullYear();
    let mes = hoy.getMonth() - nacimiento.getMonth();
    if (mes < 0 || (mes === 0 && hoy.getDate() < nacimiento.getDate())) edad--;
    return edad + " años";
}

