    font-display: swap;
}

html {
    height: 100vh;
    overflow: hidden;
}

body {
    overflow-x: hidden;
    overflow-y: hidden;
}

::-webkit-scrollbar {
    width: 8px !important;
    height: 8px !important;
    background: transparent !important;
}

