    border-radius: 4px!important;
}

.tooltip-inner {
    background-color: #4d4d4f !important;
    font-size: 12px;
    font-weight: 600;
    line-height: 16px;
    letter-spacing: 0.7;
}

.tooltip-arrow {
    visibility: hidden;
}

// tooltip plan card
.tooltip-bottom {
    position: absolute;// Asegúrate de que esté posicionado correctamente
    background-color: #4d4d4f !important;
    color: white;
    border-radius: 4px;
    text-align: center;
    padding: 4px 8px 4px 8px !important;
    z-index: 100;
    transform: translateX(-50%);
    size: 12px;
}

.tooltip-icon-bottom {
    display: none;
    position: absolute !important;
    top: 100%;
    left: 50%;
}

.tooltip-x {
    display: none;
    position: absolute;
    width: max-content;
    background: rgba($color: $black, $alpha: 0.7) !important;
    color: $white;
    font-size: 12px;
    border-radius: 4px;
    padding: 8px 12px !important;
    z-index: 100;

    & span {
        color: $white;
    }

    &.tooltip-x-left {
        left: 0;
    }

    &.tooltip-x-bottom {
        bottom: -69%;
    }
}

.pointer {
    cursor: pointer !important;
}

.cursor-grab {
    cursor: grab !important;
}

#imagen-usuario {
    width: 50px;
