// Tabs y pestañas desde componentes
// Tabs horizontales
.nav-item-horizontal {
    background: #FFFFFF;
    padding: 12px 16px;
    border: 0px;
    border-bottom: 1px solid $ps-gray-80;
    cursor: pointer;
    text-decoration: none!important;
}

