    background-color: $ps-gray-80;
}

.nav-item-horizontal.active {
    background-color: $ps-blue-80;
}

.nav-item-horizontal.active:hover {
    background-color: $ps-blue-70;
}

.nav-item-horizontal.active:active {
    background-color: $ps-blue-60;
}

.nav-item-horizontal[aria-disabled="true"] {
    background-color: transparent!important;
    cursor: default;
}

