// INPUT SWITCH (CHECKBOX)
.custom-switch {
    position: relative;
    display: inline-block;
    width: 44px;
    height: 24px;
}

.custom-switch-input {
    opacity: 0;
    width: 0;
    height: 0;
}

.custom-switch-label {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: white;
    border: 2px solid $ps-gray-30;
    border-radius: 24px;
    cursor: pointer;
    transition: background-color 0.3s ease, border-color 0.3s ease;
}

  .custom-switch-label:hover {
    background-color: $ps-gray-90!important;
  }

  .custom-switch-label:active {
    background-color: $ps-gray-80!important;
  }

  .custom-switch-label::before {
    content: "";
    position: absolute;
    height: 18px;
    width: 18px;
    left: 2px;
    bottom: 1.5px;
    background-color: $ps-gray-30;
    border-radius: 50%;
    transition: transform 0.3s ease, background-color 0.3s ease;
}

.custom-switch-input:checked + .custom-switch-label {
    background-color: $ps-blue-40;
    border-color: $ps-blue-40;
}

  .custom-switch-input:checked + .custom-switch-label:hover {
    border-color: $ps-blue-50!important;
    background-color: $ps-blue-50!important;
  }

  .custom-switch-input:checked + .custom-switch-label:active {
    border-color: $ps-blue-30!important;
    background-color: $ps-blue-30!important;
  }

  .custom-switch-input:checked:disabled + .custom-switch-label {
    border-color: $ps-gray-60!important;
    background-color: $ps-gray-60!important;
  }

  .custom-switch-input:checked + .custom-switch-label::before {
    transform: translateX(20px);
    background-color: white;
}

// Diseño de los placeholders
input::placeholder,
select::placeholder,
textarea::placeholder {
    color: $ps-gray-50;
    font-weight: 400;
}

// Diseño de los inputs, selects y textareas en modo lectura
input.readonly,
select.readonly,
textarea.readonly {
    background: $ps-gray-90;
    color: $ps-gray-30;

    & ~ label,
    & ~ label.label-flotante {
        color: $ps-gray-30;
    }

    &:hover,
    &:focus {
        border-color: $ps-gray-60;
    }
}

.input-container select.select2.select2-vacio ~ .label-flotante {
    position: absolute;
    left: 12px;
    top: 50%;
    transform: translateY(-50%);
    transition: all .3s ease;
    pointer-events: none;
    color: #909093;
    font-size: 16px;
}

// Diseño de select2
.select2-container {
    height: 44px;
    border: none;
    color: $ps-gray-30;
    box-sizing: border-box;

    &.select2-container--default {
        width: 100% !important;
    }
}

.select2-selection.select2-selection--single,
.select2-selection__rendered {
    height: 44px;
    outline: none;
}

.select2-selection.select2-selection--single,
.select2-selection.select2-selection--multiple {
    border: 1px solid $ps-gray-40;
    border-radius: 8px;
    background: $body-bg;
    transition: all 0.3s ease;

    &:hover {
        border-color: $ps-gray-30;
    }
}

.input-container select:disabled ~ .select2-container--default .select2-selection--single .select2-selection__arrow {
    filter: opacity(0.3);
}

.input-container select:disabled ~ label,
.input-container select:disabled ~ .select2-container .select2-selection--single .select2-selection__rendered,
.input-container select:disabled ~ .select2-container .select2-selection--multiple .select2-selection__rendered {
    color: $ps-gray-50;
}

.select2-container--default.select2-container--open.select2-container--below .select2-selection--single,
.select2-container--default.select2-container--open.select2-container--below .select2-selection--multiple {
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}

