.select2-container .select2-selection--single .select2-selection__rendered {
    padding: 6px 12px;
    display: flex;
    align-items: center;
    width: calc(100% - 30px);
}

// Icono de flecha
.select2-container--default .select2-selection--single .select2-selection__arrow {
    top: 20%;
    right: 8px;
    background: url("/public/iconos/chevron-down.svg") no-repeat center center;
    background-size: 16px;

    b {
        display: none;
    }
}

// Lista desplegable
.select2-dropdown.select2-dropdown--below {
    border: 1px solid $ps-gray-40;
    background: $body-bg;
    border-radius: 8px;
    box-shadow: 0px 4px 8px 1px rgba(0, 0, 0, 0.25);
}

ul.select2-results__options li:last-child,
.select2-results {
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}

// Hover en la lista desplegable
.select2-results__option--highlighted[aria-selected] {
    background: rgba($color: $ps-gray-40, $alpha: 0.12) !important;
    color: $ps-gray-30 !important;
}

// Elemento seleccionado de la lista desplegable
.select2-container--default .select2-results__option--selected {
    background: rgba($color: $ps-gray-30, $alpha: 0.16);
    color: $ps-gray-30;
}

// Diseño de los select2 cuando tienen error
.input-container.error .select2-selection.select2-selection--single,
.input-container.error .select2-selection.select2-selection--multiple {
    border-color: $ps-state-red;

    &:hover {
        border-color: $ps-state-red;
    }
}

.input-container.error .select2-container--focus + label,
.input-container.error .select2-container--open + label,
.input-container.error select.select2:valid + label {
    color: $ps-state-red;
}

// Diseño de los select2 cuando están deshabilitados
.select2-container--default .select2-selection--single[aria-disabled="true"],
.select2-container--default .select2-selection--multiple[aria-disabled="true"] {
    border-color: $ps-gray-50;
    background: $ps-gray-90;

    &:hover {
        border-color: $ps-gray-50;
    }
}

// Select2 múltiples
.select2-container:has(.selection .select2-selection--multiple) {
    height: auto;
}

.select2-container--default .select2-selection--multiple .select2-selection__choice {
    vertical-align: top;
    margin-top: 8px;
    padding: 4px 16px 4px 12px;
    background: $ps-gray-80;
    border: none;
    border-radius: 50px;
    font-size: 14px;
    font-weight: $fs-semi-bold;
    line-height: 20px;
    letter-spacing: 0.07px;
    color: $ps-gray-30;
    cursor: pointer;

    &:hover {
        background: #dbdbe0;
    }

    &:active {
        background: #cdcdd1;
    }
}

.select2-container--default .select2-selection--multiple .select2-selection__choice__display {
    padding-left: 0;
    padding-right: 8px;
    cursor: pointer;
}

.select2-container--default .select2-selection--multiple .select2-selection__choice__remove {
    left: unset;
    right: 0;
    height: 100%;
    width: 0% !important;
    border-right: none;

    span {
        visibility: hidden;

        &:before {
            position: absolute;
            content: "\F62A";
            font-family: "bootstrap-icons";
            display: inline-block;
            color: $ps-gray-30;
            font-size: 20px;
            right: 4px;
            font-weight: 200;
            line-height: 22px;
            visibility: visible!important;

            &:hover {
                background: none;
            }
        }
    }

    &:hover {
        background: none;
    }
}

.flotante-select2 {
    color: $ps-gray-30 !important;
    top: 8px !important;
    left: 8px !important;
    padding: 0 4px !important;
    transform: translateY(-100%) !important;
    background: $body-bg !important;
}

