   172→/**
   173→ * Actualiza el input de rango doble y la barra de progreso.
   174→ *
   175→ * @param {HTMLElement} elemento Elemento que disparó el evento.
   176→ * @returns {void}
   177→ * @author Andres Morales
   178→ */
   179→function actualizarInputRangoDoble(elemento) {
   180→    const $rangeMin = $(elemento).closest('.input-rango-contenedor').find('.input-rango-min');
   181→    const $rangeMax = $(elemento).closest('.input-rango-contenedor').find('.input-rango-max');
   182→    let min = parseInt($rangeMin.val());
   183→    let max = parseInt($rangeMax.val());
   184→    if (min >= max) {
   185→        if (elemento.classList.contains('input-rango-min')) {
   186→            min = max - 1;
   187→            $rangeMin.val(min);
   188→        } else {
   189→            max = min + 1;
   190→            $rangeMax.val(max);
   191→        }
   192→    }
   193→
   194→    $(elemento).closest('.input-rango-contenedor').find('.barra-progreso-input-rango').css('background', `linear-gradient(to right, #F73B3B 0%, #F73B3B ${min}%, #FFD12E ${min}%, #FFD12E ${max}%, #81BF45 ${max}%, #81BF45 100%)`);
   195→    $('.texto-rango-min').text(`Baja 0 a ${min} %`)
   196→    $('.texto-rango-medio').text(`Media ${min + 1} a ${max} %`)
   197→    $('.texto-rango-max').text(`Alta ${(max == 100) ? max : max + 1} a 100 %`)
   198→}
   199→
   200→/**
   201→ * Crea el componente de para cambiar avatar el usaurio.
   202→ *
   203→ * @param {object} componente Datos del componente.
   204→ * @returns {string} Componente para cambiar avatar el usaurio.
   205→ * @author Andres Morales
   206→ * @author Andres Gonzalez
   207→ */
   208→function avatarUsuarioAcciones(componente) {
   209→    if (componente.recargar_pagina !== undefined) {
   210→        recargar_pagina = componente.recargar_pagina
   211→    }
   212→    return `
   213→        <div class="w-auto my-auto">
   214→            <div class="dropdown">
   215→                <div class="container-avatar-usuario-acciones dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
   216→                    <div ${crearTooltip({placement: `top`, content: `Cambiar imagen`, toggle: `popover` })}>
   217→                        <div class="circulo-avatar-usuario-acciones">
   218→                            <img ${componente.imagen ? `src="${componente.imagen}"` : ''} alt="Avatar" id="archivo-avatar-usuario-acciones-imagen_previsualizacion" class="rounded-circle">
   219→                        </div>
   220→                        <div class="camara-icon-avatar-usuario-acciones">
   221→                            <i class="bi bi-camera text-white"></i>
   222→                        </div>
   223→                    </div>
   224→                </div>
   225→                <ul class="dropdown-menu mt-3">
   226→                    <li><label class="dropdown-item text-decoration-none text-gray-10" for="imagen-archivo-avatar-usuario-acciones" onclick="accionarImagenArchivoAvatarUsuarioAcciones()">Cargar imagen perfil</label></li>
   227→                    <li><button class="dropdown-item text-decoration-none text-gray-10" onclick="abrirModalTomarFotoAvatarUsuarioAcciones()" type="button">Tomar foto de perfil</button></li>
   228→                </ul>
   229→            </div>
   230→            <input type="file" id="imagen-archivo-avatar-usuario-acciones" class="input-recorte" hidden data-imagen="archivo-avatar-usuario-acciones-imagen" accept="image/*">
   231→            <textarea id="archivo-avatar-usuario-acciones-imagen" name="archivo-avatar-usuario-acciones-imagen" hidden class="d-none"></textarea>
   232→            ${crearModal({
   233→                id: 'modal-tomar-foto-avatar-usuario-acciones',
   234→                adiconales_modals_header: 'data-bs-backdrop="static" data-bs-keyboard="false"',
   235→                dialog_class: 'modal-fullscreen-sm-down modal-md modal-dialog-centered',
   236→                titulo_header: 'Foto de perfil',
   237→                boton_footer1: {
   238→                    type: 'button',
   239→                    texto: 'Cerrar',
   240→                    class: 'btn-outline-primary col-12 col-md mt-3',
   241→                    cerrar_modal: true,
   242→                },
   243→                boton_footer2: {
   244→                    type: 'button',
   245→                    id: 'boton_tomar_foto',
   246→                    disabled : true,
   247→                    texto: 'Agregar fotografia',
   248→                    class: 'btn-primary col-12 col-md mt-3',
   249→                    cerrar_modal: false,
   250→                    onclick: 'guardarFotoAvatarUsuarioAcciones()',
   251→                },
   252→                modal_body: `
   253→                    <input type="hidden" id="id-avatar-usuario-acciones" value="${componente.id}">
   254→                    <input type="hidden" id="foto-avatar-usuario-acciones">
   255→                    <div class="card">
   256→                        <div class="card-body">
   257→                            <div class="col-12 my-3 d-flex flex-column justify-content-center">
   258→                                <div class="mx-auto mt-2">
   259→                                    <video id="camara-avatar-usuario-acciones" autoplay muted playsinline class="camara-avatar-usuario-acciones"></video>
   260→                                    <img id="foto-captura-avatar-usuario-acciones" class="camara-avatar-usuario-acciones" style="display: none;">
   261→                                </div>
   262→                            </div>
   263→                            <div class="col-12 my-3 d-flex justify-content-center">
   264→                                ${crearBoton({
   265→                                    type: 'button',
   266→                                    id: 'capturar-foto-avatar-usuario-acciones',
   267→                                    clases: 'btn-tonal-primary text-label-1',
   268→                                    texto: 'Tomar foto',
   269→                                    onclick: 'tomarCapturaFotoAvatarUsuarioAcciones()',
   270→                                })}
   271→                            </div>
   272→                            <div class="col-12 my-3">
   273→                                <p class="text-body-2 text-gray-30">
   274→                                    Recomendaciones
   275→                                </p>
   276→                                <ul class="text-body-2 text-gray-30">
   277→                                    <li>Asegúrate de estar bien iluminado.</li>
   278→                                    <li>Si no ves tu imagen, prueba con otro dispositivo.</li>
   279→                                    <li>No bloquees la cámara.</li>
   280→                                </ul>
   281→                            </div>
   282→                        </div>
   283→                    </div>
   284→                `
   285→            })}
   286→        </div>
   287→    `;
   288→}
   289→
   290→var stream_avatar_usuario_acciones = null;
   291→
   292→/**
   293→ * Crea el componente de Abre la modal para tomar la foto.
   294→ *
   295→ * @param {object} componente Datos del componente.
   296→ * @returns {string} Componente Abre la modal para tomar la foto.
   297→ * @author Andres Morales
   298→ */
   299→function abrirModalTomarFotoAvatarUsuarioAcciones() {
   300→    navigator.mediaDevices.getUserMedia({ video: true, audio: false })
   301→        .then(function (mediaStream) {
   302→            stream_avatar_usuario_acciones = mediaStream;
   303→            $('#camara-avatar-usuario-acciones')[0].srcObject = mediaStream;
   304→
   305→            mediaStream.getTracks().forEach(function(track) {
   306→                track.onended = function () {
   307→                };
   308→
   309→                track.oninactive = function () {
   310→                };
   311→            });
   312→        })
   313→        .catch(function (error) {
   314→            $('#solicitar_permisos').attr('disabled', false);
   315→            mostrarToast('danger', 'Debes permitir el uso de la cámara y micrófono para presentar tus pruebas.');
   316→        });
   317→    $('#modal-tomar-foto-avatar-usuario-acciones').modal('show');
   318→    $('#modal-tomar-foto-avatar-usuario-acciones').off('hidden.bs.modal').on('hidden.bs.modal', function () {
   319→        if (stream_avatar_usuario_acciones) {
   320→            stream_avatar_usuario_acciones.getTracks().forEach(track => track.stop());
   321→            document.getElementById('camara-avatar-usuario-acciones').srcObject = null;
   322→            stream_avatar_usuario_acciones = null;
   323→        }
   324→    });
   325→}
   326→
   327→/**
   328→ * Crea el componente de toma la captura del usuario.
   329→ *
   330→ * @param {object} componente Datos del componente.
   331→ * @returns {string} Componente toma la captura del usuario.
   332→ * @author Andres Morales
   333→ * @author Andres Gonzalez
   334→ */
   335→function tomarCapturaFotoAvatarUsuarioAcciones() {
   336→    if (!stream_avatar_usuario_acciones) {
   337→        alert('Primero debes iniciar la cámara.');
   338→        return;
   339→    }
   340→
   341→    let video = $('#camara-avatar-usuario-acciones')[0];
   342→    let lienzo = document.createElement('canvas');
   343→    lienzo.width = 500;
   344→    lienzo.height = 500;
   345→
   346→    let contexto = lienzo.getContext('2d');
   347→
   348→    // Tomar el centro del video
   349→    let tamano_fuente = Math.min(video.videoWidth, video.videoHeight);
   350→    let inicio_x = (video.videoWidth - tamano_fuente) / 2;
   351→    let inicio_y = (video.videoHeight - tamano_fuente) / 2;
   352→
   353→    contexto.drawImage(
   354→        video,
   355→        inicio_x, inicio_y, tamano_fuente, tamano_fuente,
   356→        0, 0, 500, 500
   357→    );
   358→
   359→    let url_datos = lienzo.toDataURL('image/png');
   360→
   361→    $('#foto-captura-avatar-usuario-acciones').attr('src', url_datos).show();
   362→    $('#camara-avatar-usuario-acciones').hide();
   363→    $('#boton_tomar_foto').prop('disabled', false);
   364→    $('#capturar-foto-avatar-usuario-acciones').text('Repetir foto').attr('onclick', 'reiniciarCamaraAvatarUsuarioAcciones()');
   365→};
   366→
   367→/**
   368→ * Crea el componente de Reinicia el compoenente para volver a tomar la foto.
   369→ *
   370→ * @param {object} componente Datos del componente.
   371→ * @returns {string} Componente Reinicia el compoenente para volver a tomar la foto.
   372→ * @author Andres Morales
   373→ * @author Andres Gonzalez
   374→ */
   375→function reiniciarCamaraAvatarUsuarioAcciones() {
   376→    $('#foto-captura-avatar-usuario-acciones').hide();
   377→    $('#camara-avatar-usuario-acciones').show();
   378→    $('#boton_tomar_foto').prop('disabled', true);
   379→    $('#capturar-foto-avatar-usuario-acciones').text('Tomar foto').attr('onclick', 'tomarCapturaFotoAvatarUsuarioAcciones()');
   380→}
   381→
   382→/**
   383→ * Crea el componente de Guarda la foto del usuario .
   384→ *
   385→ * @param {boolean} entrada2 booleano.
   386→ * @returns {string} Componente Guarda la foto del usuario .
   387→ * @author Andres Morales
   388→ * @author Andres Gonzalez
   389→ */
   390→function guardarFotoAvatarUsuarioAcciones(entrada2 = true) {
   391→    var id = $('#id-avatar-usuario-acciones').val();
   392→    if (entrada2) {
   393→        var foto = $('#foto-captura-avatar-usuario-acciones').attr('src');
   394→    } else {
   395→        var foto = $('#archivo-avatar-usuario-acciones-imagen_previsualizacion').attr('src');
   396→    }
   397→
   398→    $.ajax({
   399→        type: 'POST',
   400→        url: '/usuario-actualizar-imagen',
   401→        data: {
   402→            _token: $('meta[name="csrf-token"]').attr('content'),
   403→            id: id,
   404→            imagen: foto
   405→        },
   406→        success: function (response) {
   407→            mostrarToast('success', response.message);
   408→            $('#archivo-avatar-usuario-acciones-imagen_previsualizacion').attr('src', foto);
   409→            $('.modal').modal('hide');
   410→            if (recargar_pagina) {
   411→                setTimeout(() => {
   412→                    location.reload();
   413→                }, 2000);
   414→            }
   415→        },
   416→        error: function () {
   417→            mostrarToast('danger', 'Error al actualizar la foto de perfil.');
   418→        }
   419→    });
   420→}
   421→
   422→/**
   423→ * Crea el componente de Cuando recorta ejecuta la accion de guardar la imagen de  usuario.
   424→ *
   425→ * @param {object} componente Datos del componente.
   426→ * @returns {string} Componente Cuando recorta ejecuta la accion de guardar la imagen de  usuario.
   427→ * @author Andres Morales
   428→ */
   429→function accionarImagenArchivoAvatarUsuarioAcciones() {
   430→    $(document).off('click', `#imagen-archivo-avatar-usuario-acciones-modal .recortar-boton`).on('click', `#imagen-archivo-avatar-usuario-acciones-modal .recortar-boton`, function () {
   431→        setTimeout(() => {
   432→            guardarFotoAvatarUsuarioAcciones(false);
   433→        }, 500);
   434→    });
   435→}
   436→
   437→/**
   438→ * Función para manejar el evento de la siguiente pregunta
   439→ *
   440→ * @returns {Promise<void>}
   441→ * @author Jair Carvajal
   442→ */
   443→function descontarTiempoLocal(tiempo) {
   444→    descuentoTiempo('tiempo_restante', parseInt(tiempo), function() {
   445→        $("#tiempo_finalizado").modal('show');
   446→        $("#reloj-alerta").parent().addClass('col-10 text-center');
   447→    },function(tiempoUsado) {
   448→        if (tiempoUsado == 0) {
   449→            if (inicio == 1) {
   450→                if (window.descontarTiempo) {
   451→                    descontarTiempo(5);
   452→                }
   453→            }
   454→            inicio = 1;
   455→        }
   456→    });
   457→}
   458→
   459→/**
   460→ * Función paramostrar una previsualización de una imagen en un modal.
   461→ *
   462→ * @param {string} url URL de la imagen a previsualizar.
   463→ * @returns {void}
   464→ * @author José Manuel Jiménez
   465→ */
   466→function mostrarPrevisualizacionImagen(url) {
   467→    let modal = $('#modal-previsualizacion-imagen');
   468→    let imagen_modal = $('#imagen-previsualizacion');
   469→    let boton_descarga_imagen = $('#descargar-imagen');
   470→    let boton_zoom_mas = $('#zoom-in');
   471→    let boton_zoom_menos = $('#zoom-out');
   472→    let boton_restaurar_zoom = $('#reset-zoom');
   473→    let escala = 1;
   474→    imagen_modal.attr('src', url).css('transform', 'scale(1)');
   475→    modal.modal('show');
   476→    $('#cerrar-modal').off('click').on('click', function() {
   477→        modal.fadeOut();
   478→    });
   479→    boton_zoom_mas.off('click').on('click', function() {
   480→        escala += 0.1;
   481→        imagen_modal.css('transform', `scale(${escala})`);
   482→    });
   483→    boton_zoom_menos.off('click').on('click', function() {
   484→        if (escala > 0.2) {
   485→            escala -= 0.1;
   486→            imagen_modal.css('transform', `scale(${escala})`);
   487→        }
   488→    });
   489→    boton_restaurar_zoom.off('click').on('click', function() {
   490→        escala = 1;
   491→        imagen_modal.css('transform', 'scale(1)');
   492→    });
   493→    boton_descarga_imagen.off('click').on('click', function() {
   494→        let enlace = document.createElement('a');
   495→        enlace.href = url;
   496→        enlace.download = url.split('/').pop() || 'imagen-descargada.jpg';
   497→        document.body.appendChild(enlace);
   498→        enlace.click();
   499→        document.body.removeChild(enlace);
   500→    });
   501→  }
