let recargar_pagina = true;

/**
 * Crea el componente de miga de pan.
 *
 * @param {object} componente Datos del componente.
 * @returns {string} Componente miga de pan.
 * @author Andres Morales
 */
function crearMigaDePan(componente) {
    let miga = '';
    if (Array.isArray(componente.migas)) {
        let contador = 0;
        componente.migas.forEach(function (item) {
            miga += `
                ${contador > 0 ? `<li class="my-auto mx-3">/</li>` : ''}
                <li class="my-auto text-body-3 ${item.active ? 'active' : ''}">
                ${item.url ? `<a href="${item.url}" class="text-body-3 text-decoration-none">${item.nombre}</a>` : item.nombre}
                </li>
            `;
            contador++;
        });
    }
    return `
         <nav class="">
            <ol class="breadcrumb m-0">
                ${componente.volver ? `<li class="breadcrumb-item my-auto me-7"><span class="text-headline-5 pointer ${componente.spanClase ? componente.spanClase : ''}" onclick="window.history.back();"><i class="bi bi-chevron-left text-primary ${componente.iconoClase ? componente.iconoClase : ''} "></i> Volver</span></li>` : ''}
                ${miga}
            </ol>
        </nav>
    `;
}

/**
 * Componente para crear el contenedor principal de la aplicación.
 *
 * @param {object} componente Datos del componente.
 * @returns {string} Componente.
 * @author Andres Morales
 */
function contenedorPrincipal(componente) {
    return `
        ${componente.cabecera ? `
            <div class="container-fluid">
                <div class="row pb-4">
                    ${componente.cabecera}
                </div>
            </div>
        ` : ''}
        <div id="subpan" class="row bg-gray-90 border-2 border-top">
            <div class="container-fluid p-6">
                ${componente.contenido}
            </div>
        </div>
    `;
}

/**
 * Componente para crear el contenedor principal de la aplicación.
 *
 * @param {object} componente Datos del componente.
 * @returns {string} Componente.
 * @author Jair Carvajal
 */
function crearCard(componente) {
    let nombre_grupo = componente.nombre_grupo ? componente.nombre_grupo : '';
    let input_renderizado = '';

    if (componente.tipo === "radio") {
        input_renderizado = `
            <div class="col-auto px-0 ${componente.hidden ? `d-none` : ''}">
                ${crearRadioButton({
                    id: `${nombre_grupo}`,
                    name: `${nombre_grupo}`,
                    type: 'radio',
                    class: `seleccion-opcion ${componente.class ? componente.class : ''}`,
                    adicional: `${componente.hidden ? `hidden` : ''}`,
                    disabled: `${componente.disabled ? `disabled` : ''}`,
                    value: componente.id,
                    onchange: componente.onchange ? componente.onchange : '',
                    checked: componente.checked ? true : false,
                })}
            </div>
        `;
    } else if (componente.tipo === "checkbox") {
        input_renderizado = `
            <div class="col-auto px-0 ${componente.hidden ? `d-none` : ''}">
                ${crearCheckbox({
                    id: `${nombre_grupo}`,
                    name: `${nombre_grupo}`,
                    type: 'checkbox',
                    class: `seleccion-opcion ${componente.class ? componente.class : ''}`,
                    adicional: `${componente.hidden ? `hidden` : ''}`,
                    disabled: `${componente.disabled ? `disabled` : ''}`,
                    value: componente.id,
                    onchange: componente.onchange ? componente.onchange : '',
                    checked: componente.checked ? true : false,
                })}
            </div>
        `;
    }

    var cards = `<label ${componente.tooltip ? componente.tooltip : ''} class="${componente.col_tamanio ? componente.col_tamanio : 'col-12'} px-5 pointer ${nombre_grupo}_content_error rounded-3 mb-2 seccion_card ${componente.adicional ? `${componente.adicional}` : ''}">
                    <div class="row align-items-center">
                        ${input_renderizado}
                        <div class="col py-4 text-headline-6 nowrap ${componente.contenido_class ? componente.contenido_class : ''}">
                            ${componente.contenido}
                        </div>
                        ${componente.contenido2 ? `
                            <div class="col-auto text-headline-6 ps-0">
                                ${componente.contenido2}
                            </div>
                        ` : ''}
                    </div>
                </label>`;

    return cards;
}

/**
 * Componente para crear un input de rango doble.
 *
 * @param {object} componente Datos del componente.
 * @returns {string} Componente.
 * @author Andres Morales
 */
function crearInputRangoDoble(componente) {
    setTimeout(() => {
        actualizarInputRangoDoble($(`#${componente.id_rango_min}`)[0]);
    }, 500);

    return `
        <div class="row">
            <div class="col-12 p-0 input-rango-contenedor">
                <div class="barra-progreso-input-rango"></div>
                <input
                    type="range"
                    min="0"
                    max="100"
                    value="${componente.valor_min ? componente.valor_min : 40}"
                    id="${componente.id_rango_min}"
                    class="input-rango-min input-dinamico input-rango-doble"
                    oninput="actualizarInputRangoDoble(this)"
                    ${componente.adicional_rango_min ? componente.adicional_rango_min : ''}
                >
                <input
                    type="range"
                    min="0"
                    max="100"
                    value="${componente.valor_max ? componente.valor_max : 70}"
                    id="${componente.id_rango_max}"
                    class="input-rango-max input-dinamico input-rango-doble"
                    oninput="actualizarInputRangoDoble(this)"
                    ${componente.adicional_rango_max ? componente.adicional_rango_max : ''}
                >
            </div>
            <div class="col-12 row">
                <div class="col-4 text-center text-body-3 text-gray-30 texto-rango-min">
                    Baja 0 a ${componente.valor_min ? componente.valor_min : 40} %
                </div>
                <div class="col-4 text-center text-body-3 text-gray-30 texto-rango-medio">
                    Media ${componente.valor_min ? componente.valor_min : 40} a ${componente.valor_max ? componente.valor_max : 70} %
                </div>
                <div class="col-4 text-center text-body-3 text-gray-30 texto-rango-max">
                    Alta ${componente.valor_max ? componente.valor_max : 40} a 100 %
                </div>
            </div>
        </div>
    `;
}

/**
 * Actualiza el input de rango doble y la barra de progreso.
 *
 * @param {HTMLElement} elemento Elemento que disparó el evento.
 * @returns {void}
 * @author Andres Morales
 */
function actualizarInputRangoDoble(elemento) {
    const $rangeMin = $(elemento).closest('.input-rango-contenedor').find('.input-rango-min');
    const $rangeMax = $(elemento).closest('.input-rango-contenedor').find('.input-rango-max');
    let min = parseInt($rangeMin.val());
    let max = parseInt($rangeMax.val());
    if (min >= max) {
        if (elemento.classList.contains('input-rango-min')) {
            min = max - 1;
            $rangeMin.val(min);
        } else {
            max = min + 1;
            $rangeMax.val(max);
        }
    }

    $(elemento).closest('.input-rango-contenedor').find('.barra-progreso-input-rango').css('background', `linear-gradient(to right, #F73B3B 0%, #F73B3B ${min}%, #FFD12E ${min}%, #FFD12E ${max}%, #81BF45 ${max}%, #81BF45 100%)`);
    $('.texto-rango-min').text(`Baja 0 a ${min} %`)
    $('.texto-rango-medio').text(`Media ${min + 1} a ${max} %`)
    $('.texto-rango-max').text(`Alta ${(max == 100) ? max : max + 1} a 100 %`)
}

/**
 * Crea el componente de para cambiar avatar el usaurio.
 *
 * @param {object} componente Datos del componente.
 * @returns {string} Componente para cambiar avatar el usaurio.
 * @author Andres Morales
 * @author Andres Gonzalez
 */
function avatarUsuarioAcciones(componente) {
    if (componente.recargar_pagina !== undefined) {
        recargar_pagina = componente.recargar_pagina
    }
    return `
        <div class="w-auto my-auto">
            <div class="dropdown">
                <div class="container-avatar-usuario-acciones dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                    <div ${crearTooltip({placement: `top`, content: `Cambiar imagen`, toggle: `popover` })}>
                        <div class="circulo-avatar-usuario-acciones">
                            <img ${componente.imagen ? `src="${componente.imagen}"` : ''} alt="Avatar" id="archivo-avatar-usuario-acciones-imagen_previsualizacion" class="rounded-circle">
                        </div>
                        <div class="camara-icon-avatar-usuario-acciones">
                            <i class="bi bi-camera text-white"></i>
                        </div>
                    </div>
                </div>
                <ul class="dropdown-menu mt-3">
                    <li><label class="dropdown-item text-decoration-none text-gray-10" for="imagen-archivo-avatar-usuario-acciones" onclick="accionarImagenArchivoAvatarUsuarioAcciones()">Cargar imagen perfil</label></li>
                    <li><button class="dropdown-item text-decoration-none text-gray-10" onclick="abrirModalTomarFotoAvatarUsuarioAcciones()" type="button">Tomar foto de perfil</button></li>
                </ul>
            </div>
            <input type="file" id="imagen-archivo-avatar-usuario-acciones" class="input-recorte" hidden data-imagen="archivo-avatar-usuario-acciones-imagen" accept="image/*">
            <textarea id="archivo-avatar-usuario-acciones-imagen" name="archivo-avatar-usuario-acciones-imagen" hidden class="d-none"></textarea>
            ${crearModal({
                id: 'modal-tomar-foto-avatar-usuario-acciones',
                adiconales_modals_header: 'data-bs-backdrop="static" data-bs-keyboard="false"',
                dialog_class: 'modal-fullscreen-sm-down modal-md modal-dialog-centered',
                titulo_header: 'Foto de perfil',
                boton_footer1: {
                    type: 'button',
                    texto: 'Cerrar',
                    class: 'btn-outline-primary col-12 col-md mt-3',
                    cerrar_modal: true,
                },
                boton_footer2: {
                    type: 'button',
                    id: 'boton_tomar_foto',
                    disabled : true,
                    texto: 'Agregar fotografia',
                    class: 'btn-primary col-12 col-md mt-3',
                    cerrar_modal: false,
                    onclick: 'guardarFotoAvatarUsuarioAcciones()',
                },
                modal_body: `
                    <input type="hidden" id="id-avatar-usuario-acciones" value="${componente.id}">
                    <input type="hidden" id="foto-avatar-usuario-acciones">
                    <div class="card">
                        <div class="card-body">
                            <div class="col-12 my-3 d-flex flex-column justify-content-center">
                                <div class="mx-auto mt-2">
                                    <video id="camara-avatar-usuario-acciones" autoplay muted playsinline class="camara-avatar-usuario-acciones"></video>
                                    <img id="foto-captura-avatar-usuario-acciones" class="camara-avatar-usuario-acciones" style="display: none;">
                                </div>
                            </div>
                            <div class="col-12 my-3 d-flex justify-content-center">
                                ${crearBoton({
                                    type: 'button',
                                    id: 'capturar-foto-avatar-usuario-acciones',
                                    clases: 'btn-tonal-primary text-label-1',
                                    texto: 'Tomar foto',
                                    onclick: 'tomarCapturaFotoAvatarUsuarioAcciones()',
                                })}
                            </div>
                            <div class="col-12 my-3">
                                <p class="text-body-2 text-gray-30">
                                    Recomendaciones
                                </p>
                                <ul class="text-body-2 text-gray-30">
                                    <li>Asegúrate de estar bien iluminado.</li>
                                    <li>Si no ves tu imagen, prueba con otro dispositivo.</li>
                                    <li>No bloquees la cámara.</li>
                                </ul>
                            </div>
                        </div>
                    </div>
                `
            })}
        </div>
    `;
}

var stream_avatar_usuario_acciones = null;

/**
 * Crea el componente de Abre la modal para tomar la foto.
 *
 * @param {object} componente Datos del componente.
 * @returns {string} Componente Abre la modal para tomar la foto.
 * @author Andres Morales
 */
function abrirModalTomarFotoAvatarUsuarioAcciones() {
    navigator.mediaDevices.getUserMedia({ video: true, audio: false })
        .then(function (mediaStream) {
            stream_avatar_usuario_acciones = mediaStream;
            $('#camara-avatar-usuario-acciones')[0].srcObject = mediaStream;

            mediaStream.getTracks().forEach(function(track) {
                track.onended = function () {
                };

                track.oninactive = function () {
                };
            });
        })
        .catch(function (error) {
            $('#solicitar_permisos').attr('disabled', false);
            mostrarToast('danger', 'Debes permitir el uso de la cámara y micrófono para presentar tus pruebas.');
        });
    $('#modal-tomar-foto-avatar-usuario-acciones').modal('show');
    $('#modal-tomar-foto-avatar-usuario-acciones').off('hidden.bs.modal').on('hidden.bs.modal', function () {
        if (stream_avatar_usuario_acciones) {
            stream_avatar_usuario_acciones.getTracks().forEach(track => track.stop());
            document.getElementById('camara-avatar-usuario-acciones').srcObject = null;
            stream_avatar_usuario_acciones = null;
        }
    });
}

/**
 * Crea el componente de toma la captura del usuario.
 *
 * @param {object} componente Datos del componente.
 * @returns {string} Componente toma la captura del usuario.
 * @author Andres Morales
 * @author Andres Gonzalez
 */
function tomarCapturaFotoAvatarUsuarioAcciones() {
    if (!stream_avatar_usuario_acciones) {
        alert('Primero debes iniciar la cámara.');
        return;
    }

    let video = $('#camara-avatar-usuario-acciones')[0];
    let lienzo = document.createElement('canvas');
    lienzo.width = 500;
    lienzo.height = 500;

    let contexto = lienzo.getContext('2d');

    // Tomar el centro del video
    let tamano_fuente = Math.min(video.videoWidth, video.videoHeight);
    let inicio_x = (video.videoWidth - tamano_fuente) / 2;
    let inicio_y = (video.videoHeight - tamano_fuente) / 2;

    contexto.drawImage(
        video,
        inicio_x, inicio_y, tamano_fuente, tamano_fuente,
        0, 0, 500, 500
    );

    let url_datos = lienzo.toDataURL('image/png');

    $('#foto-captura-avatar-usuario-acciones').attr('src', url_datos).show();
    $('#camara-avatar-usuario-acciones').hide();
    $('#boton_tomar_foto').prop('disabled', false);
    $('#capturar-foto-avatar-usuario-acciones').text('Repetir foto').attr('onclick', 'reiniciarCamaraAvatarUsuarioAcciones()');
};

/**
 * Crea el componente de Reinicia el compoenente para volver a tomar la foto.
 *
 * @param {object} componente Datos del componente.
 * @returns {string} Componente Reinicia el compoenente para volver a tomar la foto.
 * @author Andres Morales
 * @author Andres Gonzalez
 */
function reiniciarCamaraAvatarUsuarioAcciones() {
    $('#foto-captura-avatar-usuario-acciones').hide();
    $('#camara-avatar-usuario-acciones').show();
    $('#boton_tomar_foto').prop('disabled', true);
    $('#capturar-foto-avatar-usuario-acciones').text('Tomar foto').attr('onclick', 'tomarCapturaFotoAvatarUsuarioAcciones()');
}

/**
 * Crea el componente de Guarda la foto del usuario .
 *
 * @param {boolean} entrada2 booleano.
 * @returns {string} Componente Guarda la foto del usuario .
 * @author Andres Morales
 * @author Andres Gonzalez
 */
function guardarFotoAvatarUsuarioAcciones(entrada2 = true) {
    var id = $('#id-avatar-usuario-acciones').val();
    if (entrada2) {
        var foto = $('#foto-captura-avatar-usuario-acciones').attr('src');
    } else {
        var foto = $('#archivo-avatar-usuario-acciones-imagen_previsualizacion').attr('src');
    }

    $.ajax({
        type: 'POST',
        url: '/usuario-actualizar-imagen',
        data: {
            _token: $('meta[name="csrf-token"]').attr('content'),
            id: id,
            imagen: foto
        },
        success: function (response) {
            mostrarToast('success', response.message);
            $('#archivo-avatar-usuario-acciones-imagen_previsualizacion').attr('src', foto);
            $('.modal').modal('hide');
            if (recargar_pagina) {
                setTimeout(() => {
                    location.reload();
                }, 2000);
            }
        },
        error: function () {
            mostrarToast('danger', 'Error al actualizar la foto de perfil.');
        }
    });
}

/**
 * Crea el componente de Cuando recorta ejecuta la accion de guardar la imagen de  usuario.
 *
 * @param {object} componente Datos del componente.
 * @returns {string} Componente Cuando recorta ejecuta la accion de guardar la imagen de  usuario.
 * @author Andres Morales
 */
function accionarImagenArchivoAvatarUsuarioAcciones() {
    $(document).off('click', `#imagen-archivo-avatar-usuario-acciones-modal .recortar-boton`).on('click', `#imagen-archivo-avatar-usuario-acciones-modal .recortar-boton`, function () {
        setTimeout(() => {
            guardarFotoAvatarUsuarioAcciones(false);
        }, 500);
    });
}

/**
 * Función para manejar el evento de la siguiente pregunta
 *
 * @returns {Promise<void>}
 * @author Jair Carvajal
 */
function descontarTiempoLocal(tiempo) {
    descuentoTiempo('tiempo_restante', parseInt(tiempo), function() {
        $("#tiempo_finalizado").modal('show');
        $("#reloj-alerta").parent().addClass('col-10 text-center');
    },function(tiempoUsado) {
        if (tiempoUsado == 0) {
            if (inicio == 1) {
                if (window.descontarTiempo) {
                    descontarTiempo(5);
                }
            }
            inicio = 1;
        }
    });
}

/**
 * Función paramostrar una previsualización de una imagen en un modal.
 *
 * @param {string} url URL de la imagen a previsualizar.
 * @returns {void}
 * @author José Manuel Jiménez
 */
function mostrarPrevisualizacionImagen(url) {
    let modal = $('#modal-previsualizacion-imagen');
    let imagen_modal = $('#imagen-previsualizacion');
    let boton_descarga_imagen = $('#descargar-imagen');
    let boton_zoom_mas = $('#zoom-in');
    let boton_zoom_menos = $('#zoom-out');
    let boton_restaurar_zoom = $('#reset-zoom');
    let escala = 1;
    imagen_modal.attr('src', url).css('transform', 'scale(1)');
    modal.modal('show');
    $('#cerrar-modal').off('click').on('click', function() {
        modal.fadeOut();
    });
    boton_zoom_mas.off('click').on('click', function() {
        escala += 0.1;
        imagen_modal.css('transform', `scale(${escala})`);
    });
    boton_zoom_menos.off('click').on('click', function() {
        if (escala > 0.2) {
            escala -= 0.1;
            imagen_modal.css('transform', `scale(${escala})`);
        }
    });
    boton_restaurar_zoom.off('click').on('click', function() {
        escala = 1;
        imagen_modal.css('transform', 'scale(1)');
    });
    boton_descarga_imagen.off('click').on('click', function() {
        let enlace = document.createElement('a');
        enlace.href = url;
        enlace.download = url.split('/').pop() || 'imagen-descargada.jpg';
        document.body.appendChild(enlace);
        enlace.click();
        document.body.removeChild(enlace);
    });
  }

