    $('.select2').each(function () {
        let $selector = $(this);
        function validarSelect2Vacio() {
            if ((!$selector.val() || $selector.val() === "") && !$selector.prop('required')){
                $selector.addClass('select2-vacio');
            } else {
                $selector.removeClass('select2-vacio');
            }
        }
        validarSelect2Vacio();
        $selector.on('change', validarSelect2Vacio);
    });
}

/**
 * Función para formatear las opciones de un select2 con iconos.
 *
 * @param {object} opcion Opción del select2.
 * @returns {object} Contenedor con el icono y el texto de la opción.
 * @author José Manuel Jiménez
 */
function formatoOpcionesHTML(opcion) {
    if (!opcion.id) return opcion.text;
    const $elemento = $(opcion.element);
    const icono = $elemento.data('icono') || '';
    const id = opcion.id;
    const $contenedor = $(`
        <div class="select2-opcion-html d-flex justify-content-between align-items-center">
            <span>${opcion.text}</span>
            <div class="select2-icon-wrapper" style="display: inline-block;">
                <i class="${icono} icono-select2-opciones-html text-state-red px-2" data-id="${id}"></i>
            </div>
        </div>
    `);
    return $contenedor;
}

$(document).on('select2:selecting', '.select2-opciones-html', function(e) {
    const target = e.params.args.originalEvent.target;
    if ($(target).hasClass('icono-select2-opciones-html')) {
        e.preventDefault();
        const id = $(target).closest('.select2-opcion-html').find('.icono-select2-opciones-html').data('id');
        const nombre = $(target).closest('.select2-opcion-html').find('span').text();
        if (window.seleccionarIconoOpcionSelect2) { seleccionarIconoOpcionSelect2(id, nombre); }
    }
});

/**
 * Limpiar las opciones de un select2.
 *
 * @param {string} input_id ID del select2.
 * @returns {void}
 * @author Daniel Beltrán
 */
function limpiarOpcionesSelect2(input_id) {
    $(`#${input_id}`).empty();
    $(`#${input_id}`).select2({
        language: mensajes_select2,
        data: []
    });

