function crearSelect2(componente) {
    let name = componente.name;
    if (componente.name && componente.multiple == true) {
        name += `[]`;
    }
    let opciones = '';
    for (let i = 0; i < componente.opciones.length; i++) {
        opciones += `
            <option
                value="${componente.opciones[i].id}"
                ${(componente.value == componente.opciones[i].id) ? 'selected' : ''}
                ${componente.opciones[i].icono ? `data-icono="${componente.opciones[i].icono}"` : ''}
                ${componente.opciones[i].accion ? `data-accion="${componente.opciones[i].accion}"` : ''}
            >
                ${componente.opciones[i].nombre}
            </option>
        `;
    }
    var select = `
        <div
            class="
                input-container
                ${componente.icono || componente.icono2 ? 'input-icon' : ''}
                ${componente.input_container_class ? componente.input_container_class : ''}
            "
            ${componente.input_container_style ? `style="${componente.input_container_style}"` : ''}
        >
            ${componente.icono ? `${componente.icono}` : ''}
            ${componente.icono2 ? `${componente.icono2}` : ''}
            <select
                ${componente.id ? `id="${componente.id}"` : ''}
                ${name ? `name="${name}"` : ''}
                class="select2 rounded-2 text-body-2 ${componente.class ? componente.class : ''}"
                ${componente.disabled ? `disabled` : ''}
                ${componente.required ? `required` : ''}
                ${componente.multiple ? `multiple` : ''}
                ${componente.onchange ? `onchange="${componente.onchange}"` : ''}
                ${componente.style ? `style="${componente.style}"` : ''}

                ${componente.adicional ? componente.adicional : ''}
                ${!!componente.buscador ? 'data-minimum-results-for-search="Infinity"' : ''}
                 `;
                if (componente.data) {
                    componente.data.forEach(val => {
                        select += ` data-${val.texto}="${val.value}" `;
                    });
                }
    select += `
            >
                ${componente.opcion_vacia == undefined || componente.opcion_vacia == true ? `<option value=""></option>` : 'vacia'}
                ${opciones}
            </select>
            ${componente.label
                ? `<label ${componente.id ? `for="${componente.id}"` : ''} class="label-flotante text-body-2" ${componente.label_style ? `style="${componente.label_style}"` : ''}>
                    ${componente.label} ${componente.required === undefined ? '' : componente.required ? '*' : '(Opcional)'}
                </label>`
                : ''
            }
        </div>
    `;
    return select;
}

/**
 * Crear un select de select2 asincrono
 *
 * @param {object} componente Datos del componente.
 * @returns {string} Componente del select2 asincrono.
 * @author José Manuel Jiménez
 */

