// Diseño de los select2 cuando tienen error
.input-container.error .select2-selection.select2-selection--single,
.input-container.error .select2-selection.select2-selection--multiple {
    border-color: $ps-state-red;

    &:hover {
        border-color: $ps-state-red;
    }
}

.input-container.error .select2-container--focus + label,
.input-container.error .select2-container--open + label,
.input-container.error select.select2:valid + label {
    color: $ps-state-red;
}

// Diseño de los select2 cuando están deshabilitados
.select2-container--default .select2-selection--single[aria-disabled="true"],
.select2-container--default .select2-selection--multiple[aria-disabled="true"] {
    border-color: $ps-gray-50;
    background: $ps-gray-90;

    &:hover {
