// Select2 múltiples
.select2-container:has(.selection .select2-selection--multiple) {
    height: auto;
}

.select2-container--default .select2-selection--multiple .select2-selection__choice {
    vertical-align: top;
    margin-top: 8px;
    padding: 4px 16px 4px 12px;
    background: $ps-gray-80;
    border: none;
    border-radius: 50px;
    font-size: 14px;
    font-weight: $fs-semi-bold;
    line-height: 20px;
    letter-spacing: 0.07px;
    color: $ps-gray-30;
    cursor: pointer;

    &:hover {
        background: #dbdbe0;
    }

    &:active {
        background: #cdcdd1;
    }
}

