    padding-left: 0;
    padding-right: 8px;
    cursor: pointer;
}

.select2-container--default .select2-selection--multiple .select2-selection__choice__remove {
    left: unset;
    right: 0;
    height: 100%;
    width: 0% !important;
    border-right: none;

    span {
        visibility: hidden;

        &:before {
            position: absolute;
            content: "\F62A";
            font-family: "bootstrap-icons";
            display: inline-block;
            color: $ps-gray-30;
            font-size: 20px;
            right: 4px;
            font-weight: 200;
            line-height: 22px;
            visibility: visible!important;

            &:hover {
                background: none;
            }
        }
    }

    &:hover {
        background: none;
    }
}

