const selects_modals = $(`#contenedor-modals .select2:not(.select2-filtro):not(.select2-opciones-html):not(.select2-container):not(.select2-hidden-accessible)${select_actualizar}`);
if (selects_modals.length > 0) {
    selects_modals.each(function() {
        $(this).select2({
            language: mensajes_select2,
            dropdownParent: $(this).closest('.modal-content') // Usa el .modal pariente más cercano
        }).on('change', function () {
            if ($(this).val() === '') {
                $(this).addClass('select2-empty');
            } else {
                $(this).removeClass('select2-empty');
            }
        });
    });
}
function formatResult(option) {
    if (!option.id) {
        return option.text;
    }
    var icon = '';

