@import "variables";

* {
    margin: 0;
    padding: 0;
    color: $ps-gray-30;
    scrollbar-width: thin !important;
    scrollbar-color: lightgrey transparent !important;
    font-family: 'Lato';
    font-display: swap;
}

html {
    height: 100vh;
    overflow: hidden;
}

body {
    overflow-x: hidden;
    overflow-y: hidden;
}

::-webkit-scrollbar {
    width: 8px !important;
    height: 8px !important;
    background: transparent !important;
}

::-webkit-scrollbar-thumb {
    background-color: lightgrey !important;
    border-radius: 4px !important;
}

::-webkit-scrollbar-button {
    display: none !important;
}

* {
    scrollbar-width: thin !important;
    scrollbar-color: lightgrey transparent !important;
}

i {
    color: inherit;
}

a.no-visited {
    text-decoration: none !important;

    &:visited {
        color: inherit !important;
    }
}

// Tooltips
.tooltip.show {
    opacity: 1 !important;
    background-color: $ps-gray-30;
    border-radius: 4px!important;
}

.tooltip-inner {
    background-color: #4d4d4f !important;
    font-size: 12px;
    font-weight: 600;
    line-height: 16px;
    letter-spacing: 0.7;
}

.tooltip-arrow {
    visibility: hidden;
}

// tooltip plan card
.tooltip-bottom {
    position: absolute;// Asegúrate de que esté posicionado correctamente
    background-color: #4d4d4f !important;
    color: white;
    border-radius: 4px;
    text-align: center;
    padding: 4px 8px 4px 8px !important;
    z-index: 100;
    transform: translateX(-50%);
    size: 12px;
}

.tooltip-icon-bottom {
    display: none;
    position: absolute !important;
    top: 100%;
    left: 50%;
}

.tooltip-x {
    display: none;
    position: absolute;
    width: max-content;
    background: rgba($color: $black, $alpha: 0.7) !important;
    color: $white;
    font-size: 12px;
    border-radius: 4px;
    padding: 8px 12px !important;
    z-index: 100;

    & span {
        color: $white;
    }

    &.tooltip-x-left {
        left: 0;
    }

    &.tooltip-x-bottom {
        bottom: -69%;
    }
}

.pointer {
    cursor: pointer !important;
}

.cursor-grab {
    cursor: grab !important;
}

#imagen-usuario {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    background-position: center center;
    background-repeat: no-repeat;
    background-size: cover;
}

.imagen-usuario-card {
    width: 72px;
    height: 72px;
    border-radius: 50%;
    background-position: center center;
    background-repeat: no-repeat;
    background-size: cover;
}

.imagen-rol-empresa {
    width: 44px;
    height: 44px;
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
}

#navbarDropdown::after {
    margin-bottom: 17px !important;
}

// Tabs y pestañas desde componentes
// Tabs horizontales
.nav-item-horizontal {
    background: #FFFFFF;
    padding: 12px 16px;
    border: 0px;
    border-bottom: 1px solid $ps-gray-80;
    cursor: pointer;
    text-decoration: none!important;
}

.nav-item-horizontal:hover {
    background-color: $ps-gray-80;
}

.nav-item-horizontal.active {
    background-color: $ps-blue-80;
}

.nav-item-horizontal.active:hover {
    background-color: $ps-blue-70;
}

.nav-item-horizontal.active:active {
    background-color: $ps-blue-60;
}

.nav-item-horizontal[aria-disabled="true"] {
    background-color: transparent!important;
    cursor: default;
}

.nav-item-horizontal[aria-disabled="true"] .nav-horizontal, a[aria-disabled="true"] i, a[aria-disabled="true"] .titulo-nav-horizontal {
    color: $ps-gray-50!important;
}

.nav-item-horizontal[aria-disabled="true"] .badge {
    background-color: $ps-gray-50!important;
}

.nav-horizontal {
    color: $ps-gray-30;
    font-weight: 400!important;
}

.nav-item-horizontal.active {
    padding: 0px 16px;
}

.nav-item-horizontal.active .nav-horizontal {
    border-top: 3px solid transparent;
    border-bottom: 3px solid $ps-blue-40;
    padding: 12px 0px;
    margin-bottom: -1px;
    margin-top: -1px;
}

.nav-item-horizontal.active i, .nav-item-horizontal.active .titulo-nav-horizontal {
    color: $ps-blue-40;
    font-weight: bold;
}

// Tabs verticales
.nav-item-vertical {
    width: 100%;
    padding: 12px 16px;
    border: 0px;
    cursor: pointer;
    text-decoration: none!important;
    border-left: 3px solid transparent;
}

.nav-item-vertical.active {
    background-color: $ps-blue-80;
    padding: 0px 16px;
    border-left: 3px solid $ps-blue-40;
}

.nav-item-vertical:hover {
    background-color: $ps-gray-80;
}

.nav-item-vertical.active:hover {
    background-color: $ps-blue-70;
}

.nav-item-vertical.active:active {
    background-color: $ps-blue-60;
}

.nav-item-vertical[aria-disabled="true"] {
    background-color: transparent!important;
    cursor: default;
}

.nav-item-vertical[aria-disabled="true"] .nav-vertical, a[aria-disabled="true"] i, a[aria-disabled="true"] .titulo-nav-vertical {
    color: $ps-gray-50!important;
}

.nav-item-vertical[aria-disabled="true"] .badge {
    background-color: $ps-gray-50!important;
}

.nav-vertical {
    width: 100%;
    color: $ps-gray-30;
    font-weight: 400!important;
}

.nav-item-vertical.active .nav-vertical {
    padding: 12px 0px;
}

.nav-item-vertical.active i, .nav-item-vertical.active .titulo-nav-vertical {
    color: $ps-blue-40;
}

