    border-radius: 4px;
    padding: 6px 12px;
}

.tag-sm {
    padding: 6px 8px;
}

.tag i {
    color: inherit;
}

.tag-filled-green {
    background-color: $ps-state-green;
    color: white;
}

.tag-filled-yellow {
    background-color: $ps-state-yellow;
    color: #00192A;
}

.tag-filled-red {
