// Botones
.btn {
    height: 44px;
    display: inline-flex;
    align-items: center;
    padding: 12px 16px;
    border-radius: 8px;

    &:hover {
        text-decoration: none;
    }
}

.btn-circle {
    display: inline-block;
    border-radius: 50% !important;
    width: 44px;
    height: 44px;
    padding: 0;
    background: $white;
    border: 1px solid transparent;

    i {
        display: flex;
        align-items: center;
        justify-content: center;

        &:before {
            font-size: 20px;
        }
    }
}

.btn-primary {
    border-color: transparent !important;
    color: $white !important;
    background-color: $ps-blue-40 !important;

    &:hover {
        background: rgba($color: $ps-blue-40, $alpha: 0.9) !important;
        box-shadow: 0 2px 3px $ps-gray-50 !important;
    }

    &:active {
        background: $ps-blue-30 !important;
        box-shadow: none !important;
    }

    &:visited {
        color: $white !important;
    }

    &:disabled {
        background: $ps-gray-50 !important;
    }
}

.btn-danger {
