// Formularios
input,
select,
textarea {
    box-shadow: none !important;
}

textarea {
    max-height: 200px;
}

// Diseño del contenedor de los checkbox
.check-container {
    $checkbox-size: 16px;
    width: calc($checkbox-size + 16px);
    height: calc($checkbox-size + 16px);
    display: inline-flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    cursor: pointer;
    margin: 4px;

    &:hover {
        background: rgba($color: $ps-gray-40, $alpha: 0.12);
    }

    &:has(input:active) {
        background: rgba($color: $ps-gray-30, $alpha: 0.16);
    }

    // Diseño de los checkbox
    input[type="checkbox"] {
        width: $checkbox-size;
        height: $checkbox-size;
        border: 2px solid $ps-gray-30;
        border-radius: 2px;
        cursor: inherit;
        position: relative;

        &:not(.form-check-input) {
            background: transparent;
            background-color: $ps-blue-40;
            border-color: $ps-blue-40;
        }

        &:checked {
            background: $ps-blue-40;
            border-color: $ps-blue-40;
        }

        &:indeterminate {
            background-color: $ps-blue-40;
            border-color: $ps-blue-40;
        }

        // Diseño del icono
        &.form-check-input:checked::before {
            position: absolute;
            content: "\F26E";
            font-family: "bootstrap-icons";
            display: inline-block;
            color: $white;
            font-size: 20px;
            line-height: 0.6;// Alineación vertical
            left: -4.2px;
        }

        &.form-check-input:checked:disabled {
            background: $ps-gray-40;
            border-color: $ps-gray-40;
        }
    }

    .form-check-input {
        margin-top: 0;
    }

    .form-check-input:active::before {
        color: $white;
    }

    // Diseño de los radios
    input[type="radio"] {
        width: $checkbox-size;
        height: $checkbox-size;
        border: 2px solid $ps-gray-30;
        border-radius: 50px;
        background: transparent;
        cursor: inherit;
        position: relative;

        &:checked {
            background: transparent;
            border-color: $ps-blue-40;
        }

        &:disabled {
            border-color: $ps-gray-50;

            &.form-check-input:checked::before {
                color: $ps-gray-50;
            }
        }

        // Diseño del icono
        &.form-check-input:checked::before {
            position: absolute;
            content: "";
            display: inline-block;
            width: 8px;
            height: 8px;
            background: $ps-blue-40;
            border-radius: 50%;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
        }

        &.form-check-input:checked:disabled::before {
            position: absolute;
            content: "";
            display: inline-block;
            width: 8px;
            height: 8px;
            background: $ps-gray-40;
            border-radius: 50%;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
        }
    }
}

.check-container label.error {
    display: none !important;
}

label.required:after {
    content: "*";
    color: $ps-gray-30;
    margin-left: 2px;
}

label.optional:after {
    content: "(Opcional)";
    color: $ps-gray-30;
    margin-left: 2px;
}

.mostrar-contrasena {
    position: relative;

    input {
        position: relative;
        padding-right: 33px;
    }

    i {
        position: absolute;
        top: 12px;
        right: 10px;
        font-size: 11pt;
        cursor: pointer;
    }
}

.input-container {
    position: relative;
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    margin-bottom: 20px;

    // Diseño base de los inputs, selects y textareas
    input,
    select,
    textarea {
        width: 100%;
        height: 44px;
        padding: 6px 12px;
        border: 1px solid $ps-gray-40;
        outline: none;
        color: $ps-gray-30;
        background: $body-bg;
        box-sizing: border-box;
        transition: all 0.3s ease;

        &:hover {
            border-color: $ps-gray-30;
        }

        &:focus {
            border-color: $ps-blue-40;
        }

        // Diseño de los label cuando el input, select o textarea esta vacio
        &:not(:placeholder-shown) ~ .label-flotante,
        &:not(:placeholder-shown) ~ .label-flotante.required:after,
        &:not(:placeholder-shown) ~ .label-flotante.optional:after {
            color: $ps-gray-30;
            top: 8px;
            left: 8px;
            padding: 0 4px;
            transform: translateY(-100%);
            font-size: 12px;
            line-height: 12px;
            background: $body-bg;
        }

        // Diseño de los label cuando el input, select o textarea tiene el foco
        &:focus ~ .label-flotante,
        &:focus ~ .label-flotante.required:after,
        &:focus ~ .label-flotante.optional:after {
            color: $ps-blue-40;
            top: 8px;
            left: 8px;
            padding: 0 4px;
            transform: translateY(-100%);
            font-size: 12px;
            line-height: 12px;
            background: $body-bg;
        }

        &:disabled {
            background: $ps-gray-90;
            color: $ps-gray-50;

            // Diseño de los label cuando el input, select o textarea está deshabilitado
            & ~ label,
            & ~ label.required:after,
            & ~ label.optional:after {
                color: $ps-gray-50;
            }

            // Diseño del hover del input, select o textarea cuando está deshabilitado
            &:hover {
                border-color: $ps-gray-60;
            }
        }

        &.error {
            border-color: $ps-state-red;
            color: $ps-state-red;

            &:focus ~ .label-flotante,
            &:focus ~ .label-flotante.required:after,
            &:focus ~ .label-flotante.optional:after {
                color: $ps-state-red;
            }
        }

        &.text-state-green:hover, &.text-state-green:focus {
            border-color: $ps-state-green;
        }
    }

    select.select2:not(:valid) ~ .label-flotante,
    select.select2.select2-empty:not([required]) ~ .label-flotante {
        position: absolute;
        left: 12px;
        top: 50%;
        transform: translateY(-50%);
        transition: all 0.3s ease;
        pointer-events: none;
        color: $ps-gray-40;
        font-size: 16px;
    }

    select.select2:disabled ~ .label-flotante {
        top: 8px;
        left: 8px;
        padding: 0 4px;
        transform: translateY(-100%);
        font-size: 12px;
        line-height: 12px;
    }

    select.select2:valid ~ .label-flotante {
        top: 8px;
        left: 8px;
        padding: 0 4px;
        transform: translateY(-100%);
        font-size: 12px;
        line-height: 12px;
        background: $body-bg;
        color: $ps-gray-30;
        z-index: 10000;
    }

    .select2-container--open ~ .label-flotante,
    .select2-container--focus ~ .label-flotante {
        top: 8px;
        left: 8px;
        padding: 0 4px;
        transform: translateY(-100%);
        font-size: 12px;
        line-height: 12px;
        background: $body-bg;
        color: $ps-blue-40;
        z-index: 10000;
    }

    select.select2:not(:valid).error ~ .select2-container .select2-selection--single {
        border-color: $ps-state-red;
    }

    select.select2:valid ~ label.error {
        display: none;
    }

    // Diseño base de los select
    select {
        padding: 6px 8px;
    }

    // Diseño base de los textarea
    textarea {
        transition: background-color 0.3s ease, color 0.3s ease,
            border-color 0.3s ease;
        resize: vertical;
        min-height: 44px;
        height: 112px;
        resize: none;
    }

    .resize-grip {
        position: absolute;
        width: 8px;
        height: 8px;
        bottom: 4px;
        right: 4px;
        background: url("/public/iconos/resizer.svg") no-repeat center center;
        background-size: 8px;
        cursor: se-resize;
    }

    // Diseño del contador de caracteres cuando el textarea está deshabilitado o en modo lectura
    textarea:disabled,
    textarea:read-only {
        & ~ .contador-caracteres {
            color: $ps-gray-50;
        }
    }

    // Diseño del label cuando está al interior del input, select o textarea
    .label-flotante {
        position: absolute;
        left: 12px;
        top: 50%;
        transform: translateY(-50%);
        transition: all 0.3s ease;
        pointer-events: none; // Permite clic en el input
        color: $ps-gray-40;
        font-weight: 400;
    }

    .textarea-flotante {
        top: 15%;
    }

    textarea::placeholder {
        color: transparent;
    }

    textarea:focus::placeholder {
        color: #aeaeb2;
    }

    &.error {
        // Diseño de los inputs, selects y textareas que tienen error y no están deshabilitados
        input:not(:disabled),
        select:not(:disabled),
        textarea:not(:disabled) {
            border-color: $ps-state-red;

            // Diseño de los label cuando el input, select o textarea tiene error
            &:focus + label.label-flotante,
            &:focus + label.label-flotante:after,
            &:not(:placeholder-shown) + label.label-flotante,
            &:not(:placeholder-shown) + label.label-flotante:after {
                color: $ps-state-red;
            }
        }

        & .label-flotante {
            color: $ps-state-red !important;
        }

        // Diseño del support text cuando el input, select o textarea tiene error
        & + small {
            color: $ps-state-red;
        }

        // Diseño del contador de caracteres cuando el textarea tiene error
        & .contador-caracteres {
            color: $ps-state-red;
        }
    }

    label.error {
        // Diseño del support text cuando el input, select o textarea tiene error
        position: absolute;
        bottom: 0;
        color: $ps-state-red !important;
        margin-bottom: -20px;
        white-space: nowrap;
        text-overflow: ellipsis;
        overflow: hidden;
        width: 100%;
        font-size: 12px;
        text-align: start;
    }

    // Diseño base del contador de caracteres
    .contador-caracteres {
        position: absolute;
        bottom: -22px;
        right: 12px;
    }

    &.input-icon {
        .input-icono1 ~ input:placeholder-shown:not(:focus) ~ .label-flotante {
            padding-left: 25px;
        }
    }

    &.input-icon {
        // Para inputs normales
        .input-icono1 ~ input:placeholder-shown:not(:focus) ~ .label-flotante {
            padding-left: 25px;
        }

        // Para elementos select con select2
        .input-icono1 ~ select.select2:not(:valid):not(:focus) ~ .label-flotante {
            padding-left: 25px;
        }
    }
}

