// Botones
.btn {
    height: 44px;
    display: inline-flex;
    align-items: center;
    padding: 12px 16px;
    border-radius: 8px;

    &:hover {
        text-decoration: none;
    }
}

.btn-circle {
    display: inline-block;
    border-radius: 50% !important;
    width: 44px;
    height: 44px;
    padding: 0;
    background: $white;
    border: 1px solid transparent;

    i {
        display: flex;
        align-items: center;
        justify-content: center;

        &:before {
            font-size: 20px;
        }
    }
}

.btn-primary {
    border-color: transparent !important;
    color: $white !important;
    background-color: $ps-blue-40 !important;

    &:hover {
        background: rgba($color: $ps-blue-40, $alpha: 0.9) !important;
        box-shadow: 0 2px 3px $ps-gray-50 !important;
    }

    &:active {
        background: $ps-blue-30 !important;
        box-shadow: none !important;
    }

    &:visited {
        color: $white !important;
    }

    &:disabled {
        background: $ps-gray-50 !important;
    }
}

.btn-danger {
    border-color: transparent !important;
    background: rgba($color: $ps-state-red, $alpha: 0.95);
    color: $white !important;

    &:hover {
        background: rgba($color: $ps-state-red, $alpha: 0.88);
        box-shadow: 0 2px 3px $ps-gray-50;
    }

    &:active {
        background: $ps-state-red !important;
        box-shadow: none;
    }

    &:disabled {
        background: $ps-gray-50;
    }
}

.btn-yellow {
    border-color: transparent !important;
    background: rgba($color: $ps-state-yellow, $alpha: 0.95);
    color: $ps-yellow-10 !important;

    &:hover {
        background: rgba($color: $ps-state-yellow, $alpha: 0.84);
        box-shadow: 0 2px 3px $ps-gray-50;
    }

    &:active {
        background: rgba($color: $ps-state-yellow, $alpha: 0.92) !important;
        box-shadow: none;
    }

    &:disabled {
        background: $ps-gray-50;
    }
}

.btn-outline-primary,
.btn-link-primary {
    border-color: $ps-blue-40 !important;
    color: $ps-blue-40 !important;

    &:hover {
        background: $ps-gray-80-hover !important;
        box-shadow: 0 2px 3px $ps-gray-50;
    }

    &:active {
        background: $ps-gray-80-pressed !important;
        box-shadow: none;
    }

    &.show {
        background: $ps-gray-80-pressed !important;
        box-shadow: none;
    }

    &:disabled {
        background: transparent;
        color: $ps-gray-50 !important;
        border-color: $ps-gray-50 !important;
    }
    &:disabled > i {
        background: transparent;
        color: $ps-gray-50 !important;
        border-color: $ps-gray-50 !important;
    }
}

.btn-outline-primary > i {
    color: $ps-blue-40 !important;
}

.btn-link-primary {
    border-color: transparent !important;

    &:hover,
    &:active {
        box-shadow: none;
    }
}

.btn-outline-danger {
