    color: $white !important;
    background-color: $ps-cta-blue !important;

    &:hover {
        background: rgba($color: $ps-cta-blue-hover, $alpha: 0.9) !important;
        box-shadow: 0 2px 3px $ps-gray-50 !important;
    }

    &:active {
        background: $ps-cta-blue-active !important;
        box-shadow: none !important;
    }

    &:visited {
        color: $white !important;
    }

    &:disabled {
        background: $ps-gray-50 !important;
        color: white !important;
    }
}

// list
.lista-primary {
    width: 100%;
    border-color: transparent !important;
    color: $white !important;
    & i, & span{
        color: $white !important;
    }
    &:hover {
