// Formularios
input,
select,
textarea {
    box-shadow: none !important;
}

textarea {
    max-height: 200px;
}

// Diseño del contenedor de los checkbox
.check-container {
    $checkbox-size: 16px;
    width: calc($checkbox-size + 16px);
    height: calc($checkbox-size + 16px);
    display: inline-flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    cursor: pointer;
    margin: 4px;

    &:hover {
        background: rgba($color: $ps-gray-40, $alpha: 0.12);
    }

    &:has(input:active) {
        background: rgba($color: $ps-gray-30, $alpha: 0.16);
    }

    // Diseño de los checkbox
    input[type="checkbox"] {
        width: $checkbox-size;
        height: $checkbox-size;
        border: 2px solid $ps-gray-30;
        border-radius: 2px;
        cursor: inherit;
        position: relative;

        &:not(.form-check-input) {
            background: transparent;
            background-color: $ps-blue-40;
            border-color: $ps-blue-40;
        }

        &:checked {
            background: $ps-blue-40;
            border-color: $ps-blue-40;
        }

        &:indeterminate {
            background-color: $ps-blue-40;
            border-color: $ps-blue-40;
        }

        // Diseño del icono
        &.form-check-input:checked::before {
            position: absolute;
            content: "\F26E";
            font-family: "bootstrap-icons";
            display: inline-block;
            color: $white;
            font-size: 20px;
            line-height: 0.6;// Alineación vertical
            left: -4.2px;
        }

        &.form-check-input:checked:disabled {
            background: $ps-gray-40;
            border-color: $ps-gray-40;
        }
    }

    .form-check-input {
        margin-top: 0;
    }

    .form-check-input:active::before {
        color: $white;
    }

    // Diseño de los radios
    input[type="radio"] {
        width: $checkbox-size;
        height: $checkbox-size;
        border: 2px solid $ps-gray-30;
        border-radius: 50px;
        background: transparent;
        cursor: inherit;
        position: relative;

        &:checked {
            background: transparent;
            border-color: $ps-blue-40;
        }

        &:disabled {
            border-color: $ps-gray-50;

            &.form-check-input:checked::before {
                color: $ps-gray-50;
            }
        }

        // Diseño del icono
        &.form-check-input:checked::before {
            position: absolute;
            content: "";
            display: inline-block;
            width: 8px;
            height: 8px;
            background: $ps-blue-40;
            border-radius: 50%;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
        }

        &.form-check-input:checked:disabled::before {
            position: absolute;
            content: "";
            display: inline-block;
            width: 8px;
            height: 8px;
            background: $ps-gray-40;
            border-radius: 50%;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
        }
    }
}

.check-container label.error {
    display: none !important;
}

label.required:after {
    content: "*";
    color: $ps-gray-30;
    margin-left: 2px;
}

label.optional:after {
    content: "(Opcional)";
