.input-container {
    position: relative;
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    margin-bottom: 20px;

    // Diseño base de los inputs, selects y textareas
    input,
    select,
    textarea {
        width: 100%;
        height: 44px;
        padding: 6px 12px;
        border: 1px solid $ps-gray-40;
        outline: none;
        color: $ps-gray-30;
        background: $body-bg;
        box-sizing: border-box;
        transition: all 0.3s ease;

        &:hover {
            border-color: $ps-gray-30;
        }

        &:focus {
            border-color: $ps-blue-40;
        }

        // Diseño de los label cuando el input, select o textarea esta vacio
        &:not(:placeholder-shown) ~ .label-flotante,
        &:not(:placeholder-shown) ~ .label-flotante.required:after,
        &:not(:placeholder-shown) ~ .label-flotante.optional:after {
            color: $ps-gray-30;
            top: 8px;
            left: 8px;
            padding: 0 4px;
            transform: translateY(-100%);
            font-size: 12px;
            line-height: 12px;
            background: $body-bg;
        }

        // Diseño de los label cuando el input, select o textarea tiene el foco
        &:focus ~ .label-flotante,
        &:focus ~ .label-flotante.required:after,
        &:focus ~ .label-flotante.optional:after {
            color: $ps-blue-40;
            top: 8px;
            left: 8px;
            padding: 0 4px;
            transform: translateY(-100%);
            font-size: 12px;
            line-height: 12px;
            background: $body-bg;
        }

        &:disabled {
            background: $ps-gray-90;
            color: $ps-gray-50;

            // Diseño de los label cuando el input, select o textarea está deshabilitado
            & ~ label,
            & ~ label.required:after,
            & ~ label.optional:after {
                color: $ps-gray-50;
            }

            // Diseño del hover del input, select o textarea cuando está deshabilitado
            &:hover {
                border-color: $ps-gray-60;
            }
        }

        &.error {
            border-color: $ps-state-red;
            color: $ps-state-red;

            &:focus ~ .label-flotante,
            &:focus ~ .label-flotante.required:after,
            &:focus ~ .label-flotante.optional:after {
                color: $ps-state-red;
            }
        }

        &.text-state-green:hover, &.text-state-green:focus {
            border-color: $ps-state-green;
        }
    }

    select.select2:not(:valid) ~ .label-flotante,
    select.select2.select2-empty:not([required]) ~ .label-flotante {
        position: absolute;
        left: 12px;
        top: 50%;
        transform: translateY(-50%);
        transition: all 0.3s ease;
        pointer-events: none;
        color: $ps-gray-40;
        font-size: 16px;
    }

    select.select2:disabled ~ .label-flotante {
        top: 8px;
        left: 8px;
        padding: 0 4px;
        transform: translateY(-100%);
        font-size: 12px;
        line-height: 12px;
    }

    select.select2[required]:valid ~ .label-flotante,
    select.select2:not([required]):not(.select2-empty) ~ .label-flotante {
        top: 8px;
        left: 8px;
        padding: 0 4px;
        transform: translateY(-100%);
        font-size: 12px;
        line-height: 12px;
        background: $body-bg;
        color: $ps-gray-30;
        z-index: 10000;
    }

    .select2-container--open ~ .label-flotante,
    .select2-container--focus ~ .label-flotante {
        top: 8px;
        left: 8px;
        padding: 0 4px;
        transform: translateY(-100%);
        font-size: 12px;
        line-height: 12px;
        background: $body-bg;
        color: $ps-blue-40;
        z-index: 10000;
    }

    select.select2:not(:valid).error ~ .select2-container .select2-selection--single {
        border-color: $ps-state-red;
    }

    select.select2:valid ~ label.error {
        display: none;
    }

    // Diseño base de los select
    select {
        padding: 6px 8px;
    }

    // Diseño base de los textarea
    textarea {
        transition: background-color 0.3s ease, color 0.3s ease,
            border-color 0.3s ease;
        resize: vertical;
        min-height: 44px;
        height: 112px;
        resize: none;
    }

    .resize-grip {
        position: absolute;
        width: 8px;
        height: 8px;
        bottom: 4px;
        right: 4px;
        background: url("/public/iconos/resizer.svg") no-repeat center center;
        background-size: 8px;
        cursor: se-resize;
    }

    // Diseño del contador de caracteres cuando el textarea está deshabilitado o en modo lectura
    textarea:disabled,
    textarea:read-only {
        & ~ .contador-caracteres {
            color: $ps-gray-50;
        }
    }

    // Diseño del label cuando está al interior del input, select o textarea
    .label-flotante {
        position: absolute;
        left: 12px;
        top: 50%;
        transform: translateY(-50%);
        transition: all 0.3s ease;
        pointer-events: none; // Permite clic en el input
        color: $ps-gray-40;
        font-weight: 400;
    }

    .textarea-flotante {
        top: 15%;
    }

    textarea::placeholder {
        color: transparent;
    }

    textarea:focus::placeholder {
        color: #aeaeb2;
    }

    &.error {
        // Diseño de los inputs, selects y textareas que tienen error y no están deshabilitados
        input:not(:disabled),
        select:not(:disabled),
        textarea:not(:disabled) {
            border-color: $ps-state-red;

            // Diseño de los label cuando el input, select o textarea tiene error
            &:focus + label.label-flotante,
            &:focus + label.label-flotante:after,
            &:not(:placeholder-shown) + label.label-flotante,
            &:not(:placeholder-shown) + label.label-flotante:after {
                color: $ps-state-red;
            }
        }

        & .label-flotante {
            color: $ps-state-red !important;
        }

        // Diseño del support text cuando el input, select o textarea tiene error
        & + small {
            color: $ps-state-red;
        }

        // Diseño del contador de caracteres cuando el textarea tiene error
        & .contador-caracteres {
            color: $ps-state-red;
        }
    }

    label.error {
        // Diseño del support text cuando el input, select o textarea tiene error
        position: absolute;
        bottom: 0;
        color: $ps-state-red !important;
        margin-bottom: -20px;
        white-space: nowrap;
        text-overflow: ellipsis;
        overflow: hidden;
        width: 100%;
        font-size: 12px;
        text-align: start;
    }

    // Diseño base del contador de caracteres
    .contador-caracteres {
        position: absolute;
        bottom: -22px;
        right: 12px;
    }

    &.input-icon {
        .input-icono1 ~ input:placeholder-shown:not(:focus) ~ .label-flotante {
            padding-left: 25px;
        }
    }

    &.input-icon {
        // Para inputs normales
        .input-icono1 ~ input:placeholder-shown:not(:focus) ~ .label-flotante {
            padding-left: 25px;
        }

        // Para elementos select con select2
        .input-icono1 ~ select.select2:not(:valid):not(:focus) ~ .label-flotante {
            padding-left: 25px;
        }
    }
}

.input-container.input-error-inline {
