    span {
        visibility: hidden;

        &:before {
            position: absolute;
            content: "\F62A";
            font-family: "bootstrap-icons";
            display: inline-block;
            color: $ps-gray-30;
            font-size: 20px;
            right: 4px;
            font-weight: 200;
            line-height: 22px;
