// Espaciado
// Esta regla genera las clases de padding desde p-1 hasta p-14
$incremento: 4px;
@for $i from 1 through 14 {
    .p-#{$i} {
        padding: #{$incremento * $i} !important;
    }

    .pt-#{$i} {
        padding-top: #{$incremento * $i} !important;
    }

    .pb-#{$i} {
        padding-bottom: #{$incremento * $i} !important;
    }

    .ps-#{$i} {
        padding-left: #{$incremento * $i} !important;
    }

    .pe-#{$i} {
        padding-right: #{$incremento * $i} !important;
    }

    .px-#{$i} {
        padding-left: #{$incremento * $i} !important;
        padding-right: #{$incremento * $i} !important;
    }

    .py-#{$i} {
        padding-top: #{$incremento * $i} !important;
        padding-bottom: #{$incremento * $i} !important;
    }
}

// Esta regla genera las clases de margin desde m-1 hasta m-36
@for $i from 1 through 36 {
    .m-#{$i} {
        margin: #{$incremento * $i} !important;
    }

    .mt-#{$i} {
        margin-top: #{$incremento * $i} !important;
    }

    .mb-#{$i} {
        margin-bottom: #{$incremento * $i} !important;
    }

    .ms-#{$i} {
        margin-left: #{$incremento * $i} !important;
    }

    .me-#{$i} {
        margin-right: #{$incremento * $i} !important;
    }

    .mx-#{$i} {
        margin-left: #{$incremento * $i} !important;
        margin-right: #{$incremento * $i} !important;
    }

    .my-#{$i} {
        margin-top: #{$incremento * $i} !important;
        margin-bottom: #{$incremento * $i} !important;
    }
}

// Redondeo
// Esta regla genera las clases de border-radius desde rounded-1 hasta rounded-8
@for $i from 1 through 8 {
    .rounded-#{$i} {
        border-radius: #{$incremento * $i} !important;
    }

    .rounded-top-#{$i} {
        border-top-left-radius: #{$incremento * $i} !important;
        border-top-right-radius: #{$incremento * $i} !important;
    }

    .rounded-bottom-#{$i} {
        border-bottom-left-radius: #{$incremento * $i} !important;
        border-bottom-right-radius: #{$incremento * $i} !important;
    }

    .rounded-start-#{$i} {
        border-top-left-radius: #{$incremento * $i} !important;
        border-bottom-left-radius: #{$incremento * $i} !important;
    }

    .rounded-end-#{$i} {
        border-top-right-radius: #{$incremento * $i} !important;
        border-bottom-right-radius: #{$incremento * $i} !important;
    }

    .rounded-top-start-#{$i} {
        border-top-left-radius: #{$incremento * $i} !important;
    }

    .rounded-top-end-#{$i} {
        border-top-right-radius: #{$incremento * $i} !important;
    }

    .rounded-bottom-start-#{$i} {
        border-bottom-left-radius: #{$incremento * $i} !important;
    }

    .rounded-bottom-end-#{$i} {
        border-bottom-right-radius: #{$incremento * $i} !important;
    }
}

