let id_eliminar = null;
let nombre_eliminar = null;
let url_eliminar = null;

/**
 * Funcin generica para inicializar el validate() y el submit() de un formulario
 *
 * @param {string} formulario id del form HTML que se desea validar.
 * @returns {void}
 * @author Jos Manuel Jimnez
 * @author Andres Morales
 * @author Andres Gonzalez
 */
function configurarFormulario(formulario) {
    let $formulario = $('#' + formulario);
    if (!$formulario.length) {
        return;
    }
    let redirige_form = $formulario.attr('data-url');
    let evitar_id_url = $formulario.data('sinIdUrl');
    if (!$formulario.data('validator')) {
        $formulario.validate();
    }
    $formulario.off("submit.formularios");
    $formulario.on("submit.formularios", function (event) {
        if ($('#' + formulario).attr('action') !== undefined) {
            resource_url = $('#' + formulario).attr('action');
        }
        $('#contenedor-alert-toast').empty();
        event.preventDefault();
        let camposHidden = $("#" + formulario + " input[type='hidden']");

        let camposHiddenVacios = camposHidden.filter(function () {
            return ($(this).val() === "" || $(this).val() === "[]") && $(this).prop('required');
        });
        let validoAdicional = true;
        if (window.validacionAdicional) { validoAdicional = validacionAdicional(formulario); }
        if ($(this).valid() && camposHiddenVacios.length == 0 && validoAdicional) {
            let id_form = $("#" + formulario + " #id").val();
            let metodo = (id_form === undefined || id_form.length == 0) ? 'POST' : 'PUT';
            if (evitar_id_url && id_form !== undefined && id_form.length > 0) {
                metodo = 'POST';
            }
            let url_form = `${resource_url}`;
            if (!evitar_id_url) {
                url_form = (id_form === undefined || id_form.length == 0) ? `${resource_url}` : `${resource_url}/${id_form}`;
            }
            let accion_realizada = (id_form === undefined || id_form.length == 0) ? 'cre' : 'actualiz';
            let datos_form = new FormData();
            if ($('#' + formulario).attr('method') !== undefined) {

